{
    "primary_diagnosis": {
        "name": "Familial Mediterranean Fever",
        "confidence": 0.8,
        "icd_code": "E85.0",
        "reasoning": "Recurrent episodes of fever, abdominal pain, and arthritis in a young woman of Mediterranean descent, with similar family history, strongly suggests FMF."
    },
    "differential_diagnoses": [
        {
            "name": "Acute intermittent porphyria",
            "confidence": 0.4,
            "icd_code": "E80.21",
            "reasoning": "Can present with recurrent abdominal pain and neurological symptoms, but less likely given fever and arthritis."
        },
        {
            "name": "Systemic lupus erythematosus",
            "confidence": 0.3,
            "icd_code": "M32.9",
            "reasoning": "Autoimmune disorder that can cause fever, arthritis, and serositis, but usually has other systemic manifestations."
        },
        {
            "name": "Hereditary angioedema",
            "confidence": 0.2,
            "icd_code": "D84.1",
            "reasoning": "Recurrent abdominal pain due to intestinal edema, but rarely causes fever or arthritis."
        }
    ],
    "key_findings": [
        "Recurrent self-limited episodes of fever, abdominal pain, and arthritis",
        "Family history of similar episodes",
        "Mediterranean ancestry"
    ],
    "diagnostic_tests": [
        {
            "test": "MEFV gene mutation analysis",
            "purpose": "Confirm diagnosis of FMF",
            "priority": "urgent"
        },
        {
            "test": "CBC with differential",
            "purpose": "Assess for leukocytosis or anemia",
            "priority": "immediate"
        },
        {
            "test": "CRP and ESR",
            "purpose": "Evaluate for acute inflammation",
            "priority": "immediate"
        },
        {
            "test": "Urinalysis",
            "purpose": "Rule out UTI or pyelonephritis",
            "priority": "immediate"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "IV fluids for hydration",
            "Antipyretics for fever control"
        ],
        "medications": [
            {
                "drug": "Colchicine",
                "dose": "0.6 mg PO BID",
                "route": "PO",
                "duration": "Lifelong",
                "rationale": "Prophylaxis against FMF attacks"
            }
        ],
        "monitoring": [
            "Vital signs q4h",
            "I/O and fluid status"
        ],
        "consultations": [
            {
                "specialty": "Rheumatology",
                "urgency": "urgent",
                "reason": "Confirm diagnosis and guide long-term management"
            },
            {
                "specialty": "Genetics",
                "urgency": "routine",
                "reason": "Counseling regarding inheritance pattern and family testing"
            }
        ]
    },
    "red_flags": [
        "Signs of peritonitis or acute abdomen",
        "Hemodynamic instability",
        "Altered mental status"
    ],
    "patient_education": [
        "FMF is a genetic disorder causing recurrent inflammatory attacks",
        "Lifelong colchicine therapy can prevent attacks and amyloidosis",
        "Discuss inheritance pattern and recommend family member screening"
    ],
    "follow_up": {
        "timing": "With rheumatology in 1-2 weeks",
        "conditions": "Worsening abdominal pain, inability to tolerate PO, signs of peritonitis"
    },
    "confidence_in_assessment": 0.8,
    "uncertainties": [
        "Confirming MEFV gene mutation",
        "Ruling out other periodic fever syndromes",
        "Ensuring medication access and adherence given language/cultural barriers"
    ]
}
