{
    "primary_diagnosis": {
        "name": "Familial Mediterranean Fever (FMF)",
        "confidence": 0.7,
        "icd_code": "M30.3",
        "reasoning": "Recurrent episodes of fever, abdominal pain, and arthritis, especially in the context of familial history and ethnicity, are highly suggestive of FMF. The lack of response to standard analgesics further supports this."
    },
    "differential_diagnoses": [
        {
            "name": "Seronegative Spondyloarthritis",
            "confidence": 0.2,
            "icd_code": "M45.4",
            "reasoning": "Arthritis with systemic features can occur in seronegative spondyloarthritis, but age of onset and familial history are less typical."
        },
        {
            "name": "Autoinflammatory Syndrome",
            "confidence": 0.1,
            "icd_code": "Unknown",
            "reasoning": "Other autoinflammatory syndromes with abdominal pain and arthritis may present similarly, but FMF is more likely given the familial pattern."
        },
        {
            "name": "Infectious Arthritis",
            "confidence": 0.05,
            "icd_code": "M00-M09",
            "reasoning": "Infection as a cause is less likely given the self-limiting nature and lack of clear infectious source."
        }
    ],
    "key_findings": [
        "Recurrent episodes of abdominal pain and fever",
        "Arthritis affecting knees and ankles",
        "Family history of similar symptoms",
        "Limited English proficiency"
    ],
    "diagnostic_tests": [
        {
            "test": "MEFV gene testing",
            "purpose": "Confirms diagnosis of FMF",
            "priority": "urgent"
        },
        {
            "test": "Complete blood count (CBC)",
            "purpose": "Rule out infection or anemia",
            "priority": "urgent"
        },
        {
            "test": "Erythrocyte Sedimentation Rate (ESR) and C-reactive Protein (CRP)",
            "purpose": "Assess inflammation",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Administer NSAIDs for pain relief",
            "Provide supportive care (hydration, rest)"
        ],
        "medications": [
            {
                "drug": "Colchicine",
                "dose": "0.6 mg orally three times daily",
                "route": "PO",
                "duration": "Ongoing",
                "rationale": "First-line medication to prevent attacks in FMF patients"
            }
        ],
        "monitoring": [
            "Pain level and fever",
            "Frequency and duration of attacks",
            "Side effects of colchicine",
            "Red blood cell count",
            "ESR and CRP levels",
            "Every 1-2 days in the immediate period"
        ],
        "consultations": [
            {
                "specialty": "Rheumatology",
                "urgency": "urgent",
                "reason": "For further evaluation and management of FMF"
            }
        ]
    },
    "red_flags": [
        "Lack of response to standard analgesics",
        "Severe abdominal pain",
        "High fever",
        "Rapid deterioration in symptoms"
    ],
    "patient_education": [
        "Explain FMF and its potential triggers",
        "Emphasize the importance of taking colchicine as prescribed",
        "Provide information on support groups and resources for FMF patients",
        "Address language barriers and provide translation services as needed"
    ],
    "follow_up": {
        "timing": "Within 1 week for follow-up appointment with rheumatologist",
        "conditions": "Return immediately if fever spikes, abdominal pain worsens, or new symptoms develop"
    },
    "confidence_in_assessment": 0.7,
    "uncertainties": [
        "Exact diagnosis requires MEFV gene testing",
        "Potential for other undiagnosed conditions contributing to symptoms"
    ]
}
