    "primary_diagnosis": {
        "name": "Familial Mediterranean fever (FMF)",
        "confidence": 0.78,
        "icd_code": "M04.1",
        "reasoning": "Recurrent self‑limited episodes of serositis (severe abdominal pain with peritoneal signs), high fevers, and transient arthritis in a young adult of African descent, with similar episodes reported in family members, strongly suggest an autosomal recessive autoinflammatory syndrome such as FMF."
    },
    "differential_diagnoses": [
        {
            "name": "Hyper IgD syndrome (Mevalonate kinase deficiency)",
            "confidence": 0.45,
            "icd_code": "D80.1",
            "reasoning": "Also presents with recurrent fevers, abdominal pain, and arthritis, but typically includes a rash and higher IgD levels; family history may be present."
        },
        {
            "name": "Crohn disease (Inflammatory bowel disease)",
            "confidence": 0.40,
            "icd_code": "K50.9",
            "reasoning": "Can cause episodic abdominal pain, fever, and peripheral arthritis; however, episodes usually last longer and are not self‑limited within days."
        },
        {
            "name": "Autoinflammatory disease, unspecified (e.g., TRAPS)",
            "confidence": 0.30,
            "icd_code": "M04.9",
            "reasoning": "Other hereditary periodic fever syndromes can mimic FMF with serositis and arthritis; lack of clear genetic data makes this a consideration."
        }
    ],
    "key_findings": [
        "Recurrent episodes of severe abdominal pain with peritoneal signs lasting 2‑3 days",
        "High fever spikes (up to 39.5°C) and transient arthritis of knees/ankles",
        "Elevated CRP and ESR during attacks",
        "Family history of similar episodic illness"
    ],
