            "test": "Abdominal ultrasound or CT abdomen",
            "purpose": "Rule out alternative intra‑abdominal pathology (e.g., appendicitis, TB peritonitis)",
            "priority": "urgent"
        },
        {
            "test": "Complete blood count, comprehensive metabolic panel",
            "purpose": "Assess for anemia, renal/hepatic function before colchicine",
            "priority": "immediate"
        },
        {
            "test": "Colonoscopy with ileal intubation if IBD suspected",
            "purpose": "Evaluate for Crohn disease if symptoms persist or imaging suggests bowel pathology",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Start colchicine 0.6 mg orally twice daily (adjust for weight and renal function)",
            "Provide intravenous fluids and analgesia (e.g., IV morphine) for acute pain control",
            "Obtain blood cultures if fever >38.5°C persists >24 h to exclude infection"
        ],
        "medications": [
            {
                "drug": "Colchicine",
                "dose": "0.6 mg PO twice daily (may increase to 1.2 mg BID if tolerated)",
                "route": "PO",
                "duration": "Long‑term maintenance",
                "rationale": "First‑line therapy for FMF; reduces attack frequency and prevents amyloidosis"
            },
            {
                "drug": "Ibuprofen",
                "dose": "600 mg PO every 6 h as needed for pain",
                "route": "PO",
                "duration": "During acute attacks",
                "rationale": "NSAID for additional analgesia and anti‑inflammatory effect"
            }
        ],
        "monitoring": [
            "CRP, ESR, and SAA weekly for the first month then monthly",
            "Renal and hepatic function tests every 3 months while on colchicine",
            "Signs of colchicine toxicity (GI upset, myopathy, cytopenias)"
        ],
        "consultations": [
            {
                "specialty": "Rheumatology",
                "urgency": "immediate",
                "reason": "Confirm diagnosis, guide colchicine dosing, consider IL‑1 inhibitor if colchicine‑resistant"
            },
            {
                "specialty": "Medical Genetics",
                "urgency": "routine",
                "reason": "Counseling regarding hereditary nature and family screening"
            },
            {
                "specialty": "Interpreter services (Somali)",
                "urgency": "immediate",
                "reason": "Ensure accurate communication for history, education, and consent"
            }
        ]
    },
    "red_flags": [
        "Persistent fever >5 days or worsening abdominal signs suggesting perforation or sepsis",
        "New onset renal insufficiency or proteinuria (possible amyloidosis)",
        "Severe abdominal distension, hypotension, or signs of peritonitis requiring surgical evaluation"
    ],
    "patient_education": [
        "Explain FMF as a genetic autoinflammatory condition and the importance of daily colchicine to prevent attacks and long‑term complications.",
        "Discuss potential side effects of colchicine and the need to report gastrointestinal upset, muscle pain, or unusual bruising.",
        "Provide information on genetic counseling for family members and the possibility of carrier testing."
