{
    "primary_diagnosis": {
        "name": "Familial Mediterranean Fever (FMF)",
        "confidence": 0.85,
        "icd_code": "E85.0",
        "reasoning": "Classic presentation of recurrent episodes with fever, abdominal pain, and arthritis in young adult of Mediterranean/East African origin with family history. Episodes are self-limiting (2-3 days), involve peritoneal signs, and affect typical joints (knees, ankles)."
    },
    "differential_diagnoses": [
        {
            "name": "Inflammatory Bowel Disease with extraintestinal manifestations",
            "confidence": 0.25,
            "icd_code": "K50.9",
            "reasoning": "Can present with abdominal pain, fever, and arthritis, but typically has more persistent GI symptoms and different episode pattern."
        },
        {
            "name": "Systemic Lupus Erythematosus",
            "confidence": 0.20,
            "icd_code": "M32.9",
            "reasoning": "Can cause episodic fever, arthritis, and serositis, but usually has other systemic features and different demographic pattern."
        },
        {
            "name": "TNF Receptor Associated Periodic Syndrome (TRAPS)",
            "confidence": 0.15,
            "icd_code": "M04.2",
            "reasoning": "Another hereditary periodic fever syndrome, but episodes typically last longer (5-21 days) and has different ethnic predominance."
        }
    ],
    "key_findings": [
        "Recurrent self-limiting episodes of fever, abdominal pain, and arthritis",
        "Family history of similar episodes",
        "Peritoneal signs during episodes",
        "Geographic/ethnic origin consistent with FMF",
        "Elevated inflammatory markers during episodes"
    ],
    "diagnostic_tests": [
        {
            "test": "MEFV gene sequencing",
            "purpose": "Confirm FMF diagnosis by identifying pathogenic mutations",
            "priority": "urgent"
        },
        {
            "test": "24-hour urine protein and microalbumin",
            "purpose": "Screen for renal amyloidosis",
            "priority": "urgent"
        },
        {
            "test": "Complete blood count, comprehensive metabolic panel, ESR, CRP",
            "purpose": "Assess current inflammatory state and organ function",
            "priority": "immediate"
        },
        {
            "test": "ANA, anti-dsDNA, complement levels",
            "purpose": "Rule out systemic lupus erythematosus",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Provide interpreter services for effective communication",
            "Symptomatic pain management for current episode",
            "Obtain detailed family history with interpreter assistance",
            "Document episode characteristics and triggers"
        ],
        "medications": [
            {
                "drug": "Colchicine",
                "dose": "0.6 mg twice daily",
                "route": "PO",
                "duration": "Long-term preventive therapy",
                "rationale": "First-line treatment for FMF prevention and amyloidosis prevention"
            },
            {
                "drug": "Ibuprofen",
                "dose": "600 mg every 8 hours as needed",
                "route": "PO",
                "duration": "During acute episodes",
                "rationale": "Symptomatic pain relief during episodes"
            }
        ],
        "monitoring": [
            "Urine protein every 6 months",
            "Kidney function every 6 months",
            "Complete blood count every 3 months initially",
            "Episode frequency and severity log"
        ],
        "consultations": [
            {
                "specialty": "Rheumatology",
                "urgency": "urgent",
                "reason": "FMF management and treatment optimization"
            },
            {
                "specialty": "Genetic Counseling",
                "urgency": "routine",
                "reason": "Family planning counseling and genetic testing interpretation"
            }
        ]
    },
    "red_flags": [
        "Development of persistent proteinuria (amyloidosis)",
        "Progressive kidney dysfunction",
        "Episodes becoming more frequent or severe",
        "Development of persistent symptoms between episodes"
