{
    "primary_diagnosis": {
        "name": "Familial Mediterranean Fever",
        "confidence": 0.9,
        "icd_code": "M04.1",
        "reasoning": "Recurrent self-resolving episodes of fever, abdominal pain with peritoneal signs, arthritis, elevated inflammatory markers, and family history in a patient from a region with higher prevalence of this autosomal recessive disorder."
    },
    "differential_diagnoses": [
        {
            "name": "Behçet's Disease",
            "confidence": 0.6,
            "icd_code": "M35.2",
            "reasoning": "Can present with recurrent abdominal pain, arthritis, and fever; consider due to geographic origin and potential oral/genital ulcers not mentioned."
        },
        {
            "name": "Systemic Lupus Erythematosus",
            "confidence": 0.4,
            "icd_code": "M32.9",
            "reasoning": "Autoimmune condition with arthritis, fever, and abdominal involvement; family history and inflammatory markers fit, but episodes are typically not self-resolving."
        },
        {
            "name": "Inflammatory Bowel Disease (Crohn's Disease)",
            "confidence": 0.5,
            "icd_code": "K50.9",
            "reasoning": "Recurrent abdominal pain and fever with elevated CRP/ESR; possible in young adults, but lack of diarrhea or weight loss lowers likelihood."
        },
        {
            "name": "Tuberculosis",
            "confidence": 0.3,
            "icd_code": "A15.9",
            "reasoning": "Endemic in Somalia; could cause recurrent fever and abdominal symptoms, but arthritis and self-resolving nature less typical."
        }
    ],
    "key_findings": [
        "Recurrent self-resolving episodes of severe abdominal pain, fever, and arthritis",
        "Family history of similar episodes",
        "Elevated CRP and ESR with peritoneal signs",
        "Patient's Somali origin suggesting genetic predisposition",
        "Limited English proficiency and no prior records"
    ],
    "diagnostic_tests": [
        {
            "test": "Genetic testing for MEFV gene mutations",
            "purpose": "Confirm FMF diagnosis",
            "priority": "urgent"
        },
        {
            "test": "Complete blood count and blood cultures",
            "purpose": "Rule out infection",
            "priority": "immediate"
        },
        {
            "test": "Abdominal ultrasound or CT",
            "purpose": "Evaluate for peritonitis or other abdominal pathology",
            "priority": "immediate"
        },
        {
            "test": "Autoantibody panel (ANA, RF)",
            "purpose": "Rule out autoimmune diseases like SLE",
            "priority": "routine"
        },
        {
            "test": "Serum amyloid A level",
            "purpose": "Assess for amyloidosis complication in FMF",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Provide interpreter services for accurate history and consent",
            "Administer IV fluids and supportive care for pain and fever"
        ],
        "medications": [
            {
                "drug": "Colchicine",
                "dose": "0.6 mg",
                "route": "PO",
                "duration": "Twice daily, lifelong if FMF confirmed",
                "rationale": "Prevents attacks in FMF and serves as diagnostic trial"
            },
            {
                "drug": "Ibuprofen",
                "dose": "400 mg",
                "route": "PO",
                "duration": "Every 6 hours as needed during episode",
                "rationale": "Anti-inflammatory for pain and fever, though noted unresponsiveness to standard analgesics"
            }
        ],
        "monitoring": [
            "Vital signs and pain level",
            "Every 4 hours during episode"
        ],
        "consultations": [
            {
                "specialty": "Rheumatology",
                "urgency": "urgent",
                "reason": "Expertise in autoinflammatory disorders like FMF"
            },
            {
                "specialty": "Genetics",
                "urgency": "routine",
                "reason": "Counseling and testing for hereditary aspects considering family history"
            },
            {
                "specialty": "Infectious Disease",
                "urgency": "urgent",
                "reason": "Rule out endemic infections from Somalia"
            }
        ]
    },
    "red_flags": [
        "Worsening peritoneal signs suggesting perforation or sepsis",
        "Persistent fever >40°C or hemodynamic instability",
        "Development of chest pain or shortness of breath indicating pleuritis"
    ],
    "patient_education": [
        "Explain potential genetic nature of condition and importance of family screening",
        "Discuss medication adherence to prevent attacks and complications like amyloidosis",
        "Advise on recognizing episode triggers and when to seek immediate care, using culturally sensitive materials"
    ],
    "follow_up": {
        "timing": "1 week after episode resolution or sooner if symptoms persist",
        "conditions": "Return immediately for worsening pain, high fever, or new joint swelling"
    },
    "confidence_in_assessment": 0.8,
    "uncertainties": [
        "Full family history details due to language barrier",
        "Potential undiagnosed complications like amyloidosis without prior records",
        "Exact prevalence of FMF in Somali population vs. other genetic fevers"
    ]
}
