        "name": "Post-traumatic stress disorder (PTSD)",
        "confidence": 0.8,
        "icd_code": "F43.10",
        "reasoning": "Symptoms of insomnia, nightmares, hypervigilance, and avoidance of trauma-related topics in the context of asylum seeking strongly suggest PTSD."
    },
    "differential_diagnoses": [
        {
            "name": "Major depressive disorder",
            "confidence": 0.6,
            "icd_code": "F32.9",
            "reasoning": "Tearfulness, difficulty concentrating, and insomnia could also indicate depression, which can co-occur with PTSD."
        },
        {
            "name": "Generalized anxiety disorder",
            "confidence": 0.4,
            "icd_code": "F41.1",
            "reasoning": "Hypervigilance and insomnia could be due to generalized anxiety, but less likely given the context of trauma and asylum seeking."
        },
        {
            "name": "Acute stress reaction",
            "confidence": 0.3,
            "icd_code": "F43.0",
            "reasoning": "If symptoms have been present for less than 1 month after the traumatic event, acute stress reaction could be considered."
        }
    ],
    "key_findings": [
        "Insomnia, nightmares, and hypervigilance suggesting PTSD",
        "Tearfulness and difficulty concentrating possibly indicating comorbid depression"
    ],
    "diagnostic_tests": [
        {
            "test": "Comprehensive metabolic panel and thyroid function tests",
            "purpose": "Rule out underlying medical causes for symptoms",
            "priority": "routine"
        },
        {
            "test": "Neuroimaging (CT or MRI of brain)",
            "purpose": "Evaluate for structural causes of headaches if severe or persistent",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Assess for safety and suicidal ideation",
            "Provide psychoeducation about PTSD and treatment options"
        ],
        "medications": [
            {
                "drug": "Sertraline",
                "dose": "50 mg daily, titrate up to 200 mg as tolerated",
                "route": "PO",
                "duration": "Minimum 6-12 months",
                "rationale": "SSRI for PTSD and possible comorbid depression"
            },
            {
                "drug": "Prazosin",
                "dose": "1 mg nightly, titrate up to 10 mg as tolerated",
                "route": "PO", 
                "duration": "Reassess after 8 weeks",
                "rationale": "For PTSD-related nightmares"
            }
        ],
        "monitoring": [
            "Symptom severity using validated scales (e.g., PCL-5)",
            "Every 2-4 weeks initially, then monthly"
        ],
        "consultations": [
            {
                "specialty": "Psychiatry",
                "urgency": "urgent",
                "reason": "For medication management and trauma-focused therapy"
            },
            {
                "specialty": "Social work",
                "urgency": "routine",
                "reason": "To assist with asylum process and connect to community resources"
            }
        ]
    },
    "red_flags": [
        "Suicidal ideation or intent",
        "Worsening symptoms despite treatment",
        "Severe dissociation or psychotic symptoms"
    ],
    "patient_education": [
        "Validate experiences and provide hope for recovery",
        "Encourage engagement in therapy and medication adherence",
        "Discuss healthy coping strategies and grounding techniques"
    ],
    "follow_up": {
        "timing": "2-4 weeks or sooner if needed",
        "conditions": "Worsening symptoms, suicidal thoughts, intolerable side effects"
    },
    "confidence_in_assessment": 0.7,
    "uncertainties": [
        "Extent of trauma history and impact on current symptoms",
