{
    "primary_diagnosis": {
        "name": "Post-Traumatic Stress Disorder (PTSD)",
        "confidence": 0.85,
        "icd_code": "F43.10",
        "reasoning": "Core symptoms of re-experiencing (nightmares), avoidance, hypervigilance, and functional impairment in a trauma-exposed asylum seeker. Cultural stigma may explain delayed presentation. Tearfulness when discussing family suggests unresolved trauma."
    },
    "differential_diagnoses": [
        {
            "name": "Major Depressive Disorder",
            "confidence": 0.5,
            "icd_code": "F33.0",
            "reasoning": "Insomnia and emotional reactivity could indicate depression, though lack of reported anhedonia/low mood and prominence of trauma-related symptoms make PTSD more likely"
        },
        {
            "name": "Adjustment Disorder with Anxiety",
            "confidence": 0.4,
            "icd_code": "F43.22",
            "reasoning": "Possible reaction to migration stressors, but symptom severity/duration exceeds typical adjustment disorder"
        },
        {
            "name": "Traumatic Brain Injury (TBI)",
            "confidence": 0.3,
            "icd_code": "S06.9",
            "reasoning": "Headaches and concentration issues could indicate prior head trauma from potential violence during displacement"
        }
    ],
    "key_findings": [
        "Avoidance of trauma discussion with emotional reactivity",
        "Hypervigilance and nightmares consistent with PTSD criteria",
        "Headaches/concentration issues requiring dual physical-psychological evaluation"
    ],
    "diagnostic_tests": [
        {
            "test": "PTSD Checklist for DSM-5 (PCL-5) via interpreter",
            "purpose": "Validate PTSD diagnosis culturally/linguistically",
            "priority": "immediate"
        },
        {
            "test": "Head CT scan",
            "purpose": "Rule out TBI given headaches and migration-related violence risk",
            "priority": "urgent"
        },
        {
            "test": "Thyroid function tests",
            "purpose": "Exclude organic causes of anxiety/insomnia",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Trauma-informed safety assessment",
            "Collaborate with interpreter/cultural liaison"
        ],
        "medications": [
            {
                "drug": "Sertraline",
                "dose": "25 mg daily increasing to 50-100 mg over 2 weeks",
                "route": "PO",
                "duration": "6-12 months minimum",
                "rationale": "First-line SSRI for PTSD with anxiety/depression comorbidity"
            },
            {
                "drug": "Prazosin",
                "dose": "1 mg at bedtime",
                "route": "PO",
                "duration": "Trial 4 weeks",
                "rationale": "Reduce trauma-related nightmares if persistent"
            }
        ],
        "monitoring": [
            "Suicidal ideation (weekly initially)",
            "Medication side effects (GI, orthostasis)"
        ],
        "consultations": [
            {
                "specialty": "Psychiatry",
                "urgency": "urgent",
                "reason": "Confirm PTSD diagnosis and complex trauma management"
            },
            {
                "specialty": "Social Work",
                "urgency": "immediate",
                "reason": "Address asylum process stressors and social determinants"
            }
        ]
    },
    "red_flags": [
        "Acute suicidal intent",
        "Dissociative episodes or psychotic features",
        "Neurological deficits (e.g., seizure)"
    ],
    "patient_education": [
        "PTSD is a normal reaction to abnormal events - not weakness",
        "Medication takes 4-6 weeks for full effect"
    ],
    "follow_up": {
        "timing": "1-2 weeks",
        "conditions": "Worsening nightmares, suicidal thoughts, or neurological symptoms"
    },
    "confidence_in_assessment": 0.8,
    "uncertainties": [
        "Exact nature/duration of trauma exposure due to avoidance",
        "Possible comorbid chronic pain from migration hardships",
        "Cultural interpretations of mental health symptoms"
    ]
}
