        {
            "name": "Somatic Symptom Disorder (or headache disorder)",
            "confidence": 0.40,
            "icd_code": "F45.1",
            "reasoning": "Persistent headaches and concentration problems may reflect somatic manifestation of psychological distress or a primary tension‑type headache (G44.1) requiring evaluation."
        }
