{
    "primary_diagnosis": {
        "name": "Glucose-6-phosphate dehydrogenase (G6PD) deficiency triggered hemolysis",
        "confidence": 0.8,
        "icd_code": "D55.0",
        "reasoning": "Middle Eastern ancestry, male sex, acute hemolytic anemia symptoms after starting semaglutide, which can trigger oxidative stress and hemolysis in G6PD deficient individuals"
    },
    "differential_diagnoses": [
        {
            "name": "Semaglutide-induced hepatotoxicity",
            "confidence": 0.4,
            "icd_code": "K71.9",
            "reasoning": "Jaundice and liver dysfunction can rarely occur as a side effect of semaglutide therapy"
        },
        {
            "name": "Autoimmune hemolytic anemia",
            "confidence": 0.3,
            "icd_code": "D59.1",
            "reasoning": "Acute onset hemolysis with jaundice and splenomegaly, but less likely given temporal relationship to starting semaglutide"
        },
        {
            "name": "Acute viral hepatitis",
            "confidence": 0.2,
            "icd_code": "B19.9",
            "reasoning": "Jaundice, fatigue and dark urine could suggest acute hepatitis, but hemolytic picture is more consistent with primary diagnosis"
        }
    ],
    "key_findings": [
        "Acute onset jaundice and dark urine",
        "Pallor, tachycardia, splenomegaly suggesting hemolysis",
        "Middle Eastern ancestry in a male patient",
        "Recent initiation of semaglutide therapy"
    ],
    "diagnostic_tests": [
        {
            "test": "Complete blood count with peripheral smear",
            "purpose": "Assess for anemia, reticulocytosis, spherocytes or bite cells consistent with hemolysis",
            "priority": "immediate"
        },
        {
            "test": "Comprehensive metabolic panel",
            "purpose": "Evaluate liver function, bilirubin (indirect>direct), LDH",
            "priority": "immediate"
        },
        {
            "test": "G6PD enzyme activity assay",
            "purpose": "Confirm diagnosis of G6PD deficiency",
            "priority": "urgent"
        },
        {
            "test": "Haptoglobin and indirect bilirubin",
            "purpose": "Additional evidence of hemolysis if low haptoglobin and high indirect bilirubin",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Discontinue semaglutide therapy",
            "Admit for close monitoring and supportive care"
        ],
        "medications": [
            {
                "drug": "Folic acid",
                "dose": "1 mg daily",
                "route": "PO",
                "duration": "Until hemolysis resolves",
                "rationale": "Support erythropoiesis during hemolysis"
            }
        ],
        "monitoring": [
            "Daily CBC to trend hemoglobin/hematocrit",
            "Repeat CMP to follow bilirubin and LFTs"  
        ],
        "consultations": [
            {
                "specialty": "Hematology",
                "urgency": "urgent",
                "reason": "Assist with diagnosis and management of hemolytic anemia"
            }
        ]
    },
    "red_flags": [
        "Rapid decline in hemoglobin",
        "Signs of worsening liver dysfunction or biliary obstruction",
        "Hemodynamic instability"
    ],
    "patient_education": [
        "Avoid semaglutide and other potential oxidative stressors in the future",
        "Genetic counseling for family members regarding G6PD status",
        "Wear medical alert bracelet indicating G6PD deficiency"
    ],
    "follow_up": {
        "timing": "Within 1 week of discharge",
        "conditions": "Worsening jaundice, pallor, fatigue, shortness of breath, dark urine, fever, abdominal pain"
    },
    "confidence_in_assessment": 0.8,
    "uncertainties": [
        "Unclear if patient had any prior episodes of hemolysis",
        "Possible concomitant liver disease exacerbating presentation"
    ]
