{
    "primary_diagnosis": {
        "name": "G6PD deficiency with drug-induced hemolytic anemia",
        "confidence": 0.85,
        "icd_code": "D55.0",
        "reasoning": "Middle Eastern ethnicity increases risk for G6PD deficiency. Semaglutide is not a classic trigger, but new medications in G6PD-deficient patients can unmask susceptibility. Presentation with jaundice, dark urine (bilirubinuria), fatigue, and splenomegaly suggests acute hemolysis."
    },
    "differential_diagnoses": [
        {
            "name": "Autoimmune hemolytic anemia",
            "confidence": 0.4,
            "icd_code": "D59.1",
            "reasoning": "Acute onset anemia with jaundice and splenomegaly could indicate immune-mediated destruction. Requires Coombs test to confirm."
        },
        {
            "name": "Semaglutide-induced hepatotoxicity",
            "confidence": 0.25,
            "icd_code": "K71.9",
            "reasoning": "Rare reports of Wegovy causing transaminase elevations. Less likely given dark urine suggests hemolysis rather than pure hepatocellular injury."
        },
        {
            "name": "Thalassemia intermedia/crisis",
            "confidence": 0.3,
            "icd_code": "D56.3",
            "reasoning": "Regional prevalence in Middle East and splenomegaly. However, acute onset after medication initiation favors hemolytic trigger over chronic hemoglobinopathy."
        }
    ],
    "key_findings": [
        "Jaundice with scleral icterus",
        "Dark urine (suggestive of hemoglobinuria/bilirubinuria)",
        "Tachycardia and pallor (signs of anemia)",
        "Recent semaglutide initiation"
    ],
    "diagnostic_tests": [
        {
            "test": "G6PD enzyme assay",
            "purpose": "Confirm G6PD deficiency",
            "priority": "immediate"
        },
        {
            "test": "Peripheral blood smear",
            "purpose": "Assess for hemolytic changes (bite cells, Heinz bodies)",
            "priority": "immediate"
        },
        {
            "test": "Reticulocyte count",
            "purpose": "Evaluate bone marrow response to anemia",
            "priority": "urgent"
        },
        {
            "test": "Direct antiglobulin test (Coombs)",
            "purpose": "Rule out autoimmune hemolysis",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Discontinue semaglutide immediately",
            "Hydration with IV fluids"
        ],
        "medications": [
            {
                "drug": "Folic acid",
                "dose": "1 mg daily",
                "route": "PO",
                "duration": "30 days",
                "rationale": "Support increased RBC production in hemolysis"
            }
        ],
        "monitoring": [
            "Hemoglobin/HCT every 6-8 hours",
            "Urine output hourly",
            "Serial bilirubin levels"
        ],
        "consultations": [
            {
                "specialty": "Hematology",
                "urgency": "immediate",
                "reason": "Confirm hemolytic etiology and guide management"
            }
        ]
    },
    "red_flags": [
        "Hemoglobin <7 g/dL requiring transfusion",
        "Development of oliguria or renal injury",
        "Neurological changes suggesting bilirubin encephalopathy"
    ],
    "patient_education": [
        "Avoid future oxidative stressors (specific medications, fava beans)",
        "Carry G6PD deficiency alert if confirmed",
        "Report dark urine or jaundice immediately with new medications"
    ],
    "follow_up": {
        "timing": "24-48 hours for lab trend reassessment",
        "conditions": "Worsening fatigue, chest pain, or decreased urine output"
    },
    "confidence_in_assessment": 0.8,
    "uncertainties": [
        "Exact trigger medication (possible undisclosed concomitant agents)",
        "Baseline G6PD status unknown",
        "Possibility of concurrent liver pathology"
    ]
}
