    "primary_diagnosis": {
        "name": "Hemolytic Anemia (likely drug-induced)",
        "confidence": 0.8,
        "icd_code": "D59.0",
        "reasoning": "Combination of jaundice, dark urine, fatigue, shortness of breath, tachycardia, and splenomegaly with recent initiation of semaglutide strongly suggests hemolysis. The medication could be triggering a G6PD deficiency or other drug-induced hemolytic reaction."
    },
    "differential_diagnoses": [
        {
            "name": "Viral Hepatitis",
            "confidence": 0.15,
            "icd_code": "B17.9",
            "reasoning": "Jaundice, fatigue, and dark urine can also be symptoms of viral hepatitis, but the patient's lack of other typical symptoms (e.g., nausea, vomiting, abdominal pain) and recent medication initiation make this less likely."
        },
        {
            "name": "Gilbert's Syndrome",
            "confidence": 0.05,
            "icd_code": "R16.0",
            "reasoning": "Uncommon inherited condition causing mild jaundice, but usually not associated with other severe symptoms or rapid onset. Less likely given the severity of presenting symptoms."
        },
        {
            "name": "Autoimmune Hemolytic Anemia",
            "confidence": 0.05,
            "icd_code": "D62.0",
            "reasoning": "Possible, but less likely without a history of other autoimmune conditions or positive blood tests for autoantibodies."
        }
    ],
    "key_findings": [
        "Jaundice with scleral icterus",
        "Dark urine (urine bilirubin likely elevated)",
        "Fatigue and shortness of breath suggestive of anemia"
    ],
    "diagnostic_tests": [
        {
            "test": "Complete Blood Count (CBC)",
            "purpose": "To confirm anemia, assess hemoglobin levels, and look for red blood cell morphology abnormalities",
            "priority": "immediate"
        },
        {
            "test": "Peripheral Blood Smear",
            "purpose": "To visualize red blood cells for signs of hemolysis (e.g., spherocytes, schistocytes)",
            "priority": "immediate"
        },
        {
            "test": "Liver Function Tests (LFTs)",
            "purpose": "To rule out liver dysfunction as a cause of jaundice",
            "priority": "urgent"
        },
        {
            "test": "Glucose-6-Phosphate Dehydrogenase (G6PD) Activity Assay",
            "purpose": "To determine if the patient has a G6PD deficiency which could be contributing to drug-induced hemolysis",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Initiate supportive care: IV fluids to address dehydration, monitor vital signs closely",
            "Review medication history and potentially discontinue semaglutide pending test results"
