{
    "primary_diagnosis": {
        "name": "G6PD deficiency with drug-induced hemolytic anemia (semaglutide-triggered)",
        "confidence": 0.85,
        "icd_code": "D55.0",
        "reasoning": "Middle Eastern ethnicity with high G6PD deficiency prevalence (up to 45% in some regions), classic hemolytic anemia presentation (jaundice, pallor, dark urine, splenomegaly), temporal relationship with semaglutide initiation. Case reports document semaglutide-induced hemolysis in G6PD-deficient Middle Eastern patients."
    },
    "differential_diagnoses": [
        {
            "name": "Autoimmune hemolytic anemia (warm type)",
            "confidence": 0.65,
            "icd_code": "D59.11",
            "reasoning": "Similar presentation with hemolysis, jaundice, and splenomegaly. Could be coincidental timing with semaglutide. Direct Coombs test will differentiate."
        },
        {
            "name": "Drug-induced immune hemolytic anemia",
            "confidence": 0.60,
            "icd_code": "D59.0",
            "reasoning": "Temporal relationship with semaglutide initiation, though this mechanism is less commonly reported with GLP-1 agonists. Would require positive direct Coombs test."
        },
        {
            "name": "Hereditary spherocytosis with hemolytic crisis",
            "confidence": 0.40,
            "icd_code": "D58.0",
            "reasoning": "Can present with similar hemolytic features and splenomegaly, but less likely given ethnic background and temporal relationship with medication."
        }
    ],
    "key_findings": [
        "Middle Eastern ethnicity (high G6PD deficiency risk)",
        "Classic hemolytic triad: jaundice, pallor, dark urine",
        "Splenomegaly indicating extravascular hemolysis",
        "Temporal relationship with semaglutide initiation",
        "Tachycardia suggesting hemodynamic compensation"
    ],
    "diagnostic_tests": [
        {
            "test": "G6PD enzyme activity level",
            "purpose": "Confirm G6PD deficiency as underlying cause",
            "priority": "immediate"
        },
        {
            "test": "CBC with differential and reticulocyte count",
            "purpose": "Assess degree of anemia and bone marrow response",
            "priority": "immediate"
        },
        {
            "test": "LDH, haptoglobin, indirect bilirubin",
            "purpose": "Confirm hemolysis and assess severity",
            "priority": "immediate"
        },
        {
            "test": "Direct Coombs test (DAT)",
            "purpose": "Rule out autoimmune hemolytic anemia",
            "priority": "urgent"
        },
        {
            "test": "Peripheral blood smear",
            "purpose": "Identify RBC morphology and assess for schistocytes, spherocytes",
            "priority": "urgent"
        },
        {
            "test": "Urinalysis with hemosiderin",
            "purpose": "Assess for intravascular hemolysis",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Discontinue semaglutide immediately",
            "Assess hemodynamic stability and oxygen saturation",
            "Type and screen blood for potential transfusion",
            "Avoid known G6PD-triggering medications and foods (fava beans)"
        ],
        "medications": [
            {
                "drug": "Folic acid",
                "dose": "5 mg",
                "route": "PO",
                "duration": "Daily until hemolysis resolves",
                "rationale": "Support increased RBC production during hemolytic episode"
            }
        ],
        "monitoring": [
            "CBC and reticulocyte count daily",
            "LDH, bilirubin, haptoglobin every 12-24 hours",
            "Urine color and output",
            "Vital signs and oxygen saturation"
        ],
        "consultations": [
            {
                "specialty": "Hematology",
                "urgency": "urgent",
                "reason": "Acute hemolytic anemia management and confirmation of G6PD deficiency diagnosis"
            }
        ]
    },
    "red_flags": [
        "Worsening anemia requiring transfusion (Hgb <7 g/dL or symptomatic)",
        "Signs of acute hemolytic crisis (rapid drop in Hgb, hemoglobinuria)",
        "Reticulocytopenia indicating inadequate bone marrow response",
        "Hemodynamic instability or signs of tissue hypoxia"
    ],
    "patient_education": [
        "G6PD deficiency is a genetic condition requiring lifelong avoidance of triggering substances",
        "Provide comprehensive list of medications and foods to avoid",
        "Importance of informing all healthcare providers about G6PD deficiency",
        "Family screening recommended as this is X-linked inherited condition"
    ],
    "follow_up": {
        "timing": "24-48 hours for lab results and clinical reassessment",
        "conditions": "Return immediately for worsening fatigue, shortness of breath, chest pain, confusion, or decreased urine output"
    },
    "confidence_in_assessment": 0.85,
    "uncertainties": [
        "Exact G6PD variant and severity until enzyme testing completed",
        "Need for blood transfusion depending on hemoglobin level and symptoms",
        "Whether semaglutide mechanism is direct oxidative stress or other pathway",
        "Presence of concurrent conditions given limited medical history"
    ]
}
