    "primary_diagnosis": {
        "name": "Glucose-6-phosphate dehydrogenase (G6PD) deficiency with acute hemolytic anemia",
        "confidence": 0.8,
        "icd_code": "D55.0",
        "reasoning": "Patient's Middle Eastern ethnicity increases risk for G6PD deficiency; symptoms of jaundice, dark urine, pallor, fatigue, shortness of breath, tachycardia, and splenomegaly suggest hemolysis; recent semaglutide start may be coincidental or overlooked trigger, but genetic predisposition must be considered despite absence in history."
    },
    "differential_diagnoses": [
        {
            "name": "Drug-induced hemolytic anemia (non-G6PD related)",
            "confidence": 0.6,
            "icd_code": "D59.2",
            "reasoning": "Recent initiation of semaglutide could directly cause immune-mediated hemolysis, though not commonly reported; timing aligns with symptom onset."
        },
        {
            "name": "Autoimmune hemolytic anemia",
            "confidence": 0.4,
            "icd_code": "D59.1",
            "reasoning": "Presents with similar hemolytic features; could be idiopathic or triggered by medication, but no fever or other autoimmune signs noted."
        },
        {
            "name": "Viral hepatitis",
            "confidence": 0.3,
            "icd_code": "B19.9",
            "reasoning": "Jaundice and dark urine common; possible in Middle Eastern patient due to regional prevalence, but hemolytic signs like splenomegaly less typical without liver failure."
        }
    ],
    "key_findings": [
        "Jaundice and dark urine indicating possible bilirubinemia or hemoglobinuria",
        "Pallor, tachycardia, shortness of breath, and fatigue suggesting anemia",
        "Mild splenomegaly consistent with hemolytic process",
        "Middle Eastern ethnicity raising suspicion for G6PD deficiency",
        "Recent semaglutide initiation as potential trigger or red herring"
    ],
    "diagnostic_tests": [
        {
            "test": "Complete blood count (CBC) with reticulocyte count",
            "purpose": "Assess for anemia, hemolysis (elevated reticulocytes)",
            "priority": "immediate"
        },
        {
            "test": "Peripheral blood smear",
            "purpose": "Look for bite cells, Heinz bodies suggestive of G6PD deficiency",
            "priority": "immediate"
        },
        {
            "test": "Serum bilirubin, LDH, haptoglobin",
            "purpose": "Confirm intravascular hemolysis (elevated indirect bilirubin, LDH; low haptoglobin)",
            "priority": "immediate"
        },
        {
            "test": "G6PD enzyme assay",
            "purpose": "Diagnose G6PD deficiency (note: may be falsely normal during acute hemolysis; repeat later if needed)",
            "priority": "urgent"
        },
        {
            "test": "Direct antiglobulin test (Coombs test)",
            "purpose": "Rule out autoimmune hemolytic anemia",
            "priority": "urgent"
        },
        {
            "test": "Hepatitis panel",
            "purpose": "Rule out viral hepatitis as cause of jaundice",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Discontinue semaglutide to prevent potential ongoing trigger",
            "Administer IV fluids for hydration and to support renal function",
            "Transfuse packed red blood cells if hemoglobin <7 g/dL or symptomatic anemia"
        ],
        "medications": [
            {
                "drug": "Folic acid",
                "dose": "1 mg",
                "route": "PO",
                "duration": "Daily indefinitely if G6PD confirmed",
                "rationale": "Supports erythropoiesis in chronic hemolytic states"
            }
        ],
        "monitoring": [
            "Vital signs and oxygen saturation, every 4 hours",
            "Hemoglobin and bilirubin levels, daily"
        ],
        "consultations": [
            {
                "specialty": "Hematology",
                "urgency": "immediate",
                "reason": "Expertise in diagnosing and managing hemolytic anemias, including G6PD deficiency"
            }
        ]
    },
    "red_flags": [
        "Worsening shortness of breath or chest pain indicating severe anemia or cardiac strain",
        "Decreasing urine output suggesting acute kidney injury from hemoglobinuria",
        "Fever or signs of infection as potential G6PD trigger"
    ],
    "patient_education": [
        "Avoid known G6PD triggers like certain medications (e.g., sulfa drugs, antimalarials) and fava beans if deficiency confirmed; discuss cultural dietary habits",
        "Report any signs of recurrent hemolysis such as dark urine or fatigue immediately",
        "Importance of genetic counseling for family members given hereditary nature"
    ],
    "follow_up": {
        "timing": "In 1 week after discharge or sooner if labs abnormal",
        "conditions": "Immediate return for worsening jaundice, severe fatigue, or shortness of breath"
