{
    "primary_diagnosis": {
        "name": "Systemic Lupus Erythematosus with Lupus Nephritis",
        "confidence": 0.85,
        "icd_code": "M32.14",
        "reasoning": "Young male with episodic hematuria, proteinuria, dysmorphic RBCs, RBC casts, hypocomplementemia (low C3/C4), positive ANA with homogeneous pattern, acute kidney injury, hypertension, and edema. The episodic nature with complete resolution between episodes is characteristic of lupus flares. Iranian heritage increases risk for SLE."
    },
    "differential_diagnoses": [
        {
            "name": "IgA Nephropathy",
            "confidence": 0.7,
            "icd_code": "N02.8",
            "reasoning": "Episodic gross hematuria in young male, especially with possible preceding infections. However, the systemic features, hypocomplementemia, and positive ANA make lupus more likely."
        },
        {
            "name": "Post-infectious Glomerulonephritis",
            "confidence": 0.4,
            "icd_code": "N05.9",
            "reasoning": "Acute nephritis with hematuria and proteinuria, but the chronic episodic nature and positive autoimmune markers argue against this diagnosis."
        },
        {
            "name": "ANCA-associated Vasculitis",
            "confidence": 0.6,
            "icd_code": "M31.30",
            "reasoning": "Can present with glomerulonephritis and systemic symptoms in young adults. Awaiting ANCA results, but positive ANA and hypocomplementemia favor lupus."
        },
        {
            "name": "Chemical-induced Nephritis",
            "confidence": 0.3,
            "icd_code": "N14.0",
            "reasoning": "Occupational exposure to organic solvents could cause renal injury, but the autoimmune markers and episodic pattern suggest primary glomerular disease."
        }
    ],
    "key_findings": [
        "Episodic gross hematuria with dysmorphic RBCs and RBC casts",
        "Significant proteinuria (3+)",
        "Acute kidney injury with decreased eGFR",
        "Hypocomplementemia (low C3 and C4)",
        "Positive ANA with homogeneous pattern",
        "Hypertension and edema suggesting volume overload",
        "Young male of Middle Eastern descent"
    ],
    "diagnostic_tests": [
        {
            "test": "Anti-dsDNA antibodies",
            "purpose": "Confirm lupus diagnosis and assess disease activity",
            "priority": "urgent"
        },
        {
            "test": "ANCA panel",
            "purpose": "Rule out ANCA-associated vasculitis",
            "priority": "urgent"
        },
        {
            "test": "Anti-Smith antibodies",
            "purpose": "Specific marker for SLE",
            "priority": "urgent"
        },
        {
            "test": "Renal biopsy",
            "purpose": "Definitive diagnosis and classification of lupus nephritis",
            "priority": "urgent"
        },
        {
            "test": "24-hour urine protein and creatinine clearance",
            "purpose": "Quantify proteinuria and assess renal function",
            "priority": "urgent"
        },
        {
            "test": "Echocardiogram",
            "purpose": "Assess for cardiac involvement and evaluate hypertension",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Admit for further evaluation and management",
            "Strict intake/output monitoring",
            "Daily weights",
            "Blood pressure monitoring and control"
        ],
        "medications": [
            {
                "drug": "Lisinopril",
                "dose": "5mg",
                "route": "oral",
                "duration": "ongoing",
                "rationale": "ACE inhibitor for hypertension and renoprotection"
            },
            {
                "drug": "Furosemide",
                "dose": "20mg",
                "route": "oral",
                "duration": "as needed",
                "rationale": "Manage fluid overload and edema"
            },
            {
                "drug": "Prednisone",
                "dose": "1mg/kg (hold pending biopsy)",
                "route": "oral",
                "duration": "variable",
                "rationale": "Immunosuppression for suspected lupus nephritis"
            }
        ],
        "monitoring": [
            "Daily creatinine and electrolytes",
            "Daily weights and fluid balance",
            "Blood pressure monitoring",
            "Urinalysis every 2-3 days",
            "Complete blood count"
        ],
        "consultations": [
            {
                "specialty": "Nephrology",
                "urgency": "immediate",
                "reason": "Acute kidney injury with suspected glomerulonephritis requiring urgent evaluation and possible biopsy"
            },
            {
                "specialty": "Rheumatology",
                "urgency": "urgent",
                "reason": "Suspected systemic lupus erythematosus requiring specialist evaluation and management"
            }
        ]
    },
    "red_flags": [
        "Rapidly declining renal function",
        "Severe hypertension",
        "Pulmonary edema",
        "Oliguria or anuria",
        "Signs of uremia",
        "Neurological symptoms suggesting CNS lupus"
    ],
    "patient_education": [
        "Explain suspected diagnosis of lupus nephritis and need for urgent evaluation",
        "Importance of medication compliance and follow-up",
        "Monitor for signs of disease flare",
        "Dietary sodium restriction",
        "Avoid nephrotoxic medications and substances"
    ],
    "follow_up": {
        "timing": "Within 48-72 hours after discharge or sooner if admitted",
        "conditions": "Return immediately for worsening kidney function, severe hypertension, difficulty breathing, decreased urine output, or neurological symptoms"
    },
    "confidence_in_assessment": 0.8,
    "uncertainties": [
        "Pending anti-dsDNA and ANCA results may alter diagnosis",
        "Renal biopsy needed for definitive classification",
        "Unclear baseline renal function",
        "Possible occupational exposure contribution unclear"
    ]
}
