{
    "primary_diagnosis": {
        "name": "Familial Mediterranean Fever",
        "confidence": 0.8,
        "icd_code": "E85.0",
        "reasoning": "Recurrent episodes of fever, abdominal pain, and arthritis in a patient of Mediterranean descent, with similar family history, suggests FMF."
    },
    "differential_diagnoses": [
        {
            "name": "Acute intermittent porphyria",
            "confidence": 0.4,
            "icd_code": "E80.21",
            "reasoning": "Can present with recurrent abdominal pain and neurological symptoms, but less likely given fever and arthritis."
        },
        {
            "name": "Systemic lupus erythematosus",
            "confidence": 0.3,
            "icd_code": "M32",
            "reasoning": "Autoimmune disorder that can cause fever, arthritis, and serositis, but episodes are usually more prolonged."
        },
        {
            "name": "Acute appendicitis",
            "confidence": 0.2,
            "icd_code": "K35",
            "reasoning": "Can cause fever and abdominal pain, but episodes are not recurrent and do not spontaneously resolve."
        }
    ],
    "key_findings": [
        "Recurrent self-limited episodes of fever, abdominal pain, and arthritis",
        "Similar episodes reported in family members",
        "Elevated inflammatory markers during episodes"
    ],
    "diagnostic_tests": [
        {
            "test": "MEFV gene sequencing",
            "purpose": "Confirm diagnosis of FMF by detecting pathogenic variants",
            "priority": "urgent"
        },
        {
            "test": "Serum amyloid A",
            "purpose": "Assess risk of amyloidosis, a serious complication of untreated FMF",
            "priority": "urgent"
        },
        {
            "test": "Urinalysis",
            "purpose": "Rule out renal amyloidosis",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Provide analgesia and antipyretics for symptomatic relief",
            "Initiate colchicine therapy"  
        ],
        "medications": [
            {
                "drug": "Colchicine",
                "dose": "1.2-2.4 mg/day in divided doses",
                "route": "PO",
                "duration": "Lifelong",
                "rationale": "Reduces frequency and severity of FMF attacks, prevents amyloidosis"
            }
        ],
        "monitoring": [
            "Response to colchicine therapy - frequency and severity of attacks",
            "Annual screening for proteinuria to detect amyloidosis"
        ],
        "consultations": [
            {
                "specialty": "Rheumatology",
                "urgency": "urgent",
                "reason": "Confirm diagnosis, guide long-term management"
            },
            {
                "specialty": "Genetic counseling",
                "urgency": "routine",
                "reason": "Discuss inheritance pattern, family testing"
            }
        ]
    },
    "red_flags": [
        "Development of proteinuria or renal insufficiency suggesting amyloidosis",
        "Intolerance or non-response to colchicine therapy"
    ],
    "patient_education": [
        "FMF is a genetic condition causing recurrent inflammatory episodes, but is manageable with lifelong colchicine therapy in most cases.",
        "Importance of adherence to colchicine to prevent amyloidosis.",
        "Encourage family members to be evaluated given autosomal recessive inheritance pattern."
    ],
    "follow_up": {
        "timing": "1-2 weeks to assess response to colchicine, then every 3-6 months if stable",
        "conditions": "Worsening attack frequency/severity, new abdominal pain, swelling, or decreased urine output"
    },
    "confidence_in_assessment": 0.7,
    "uncertainties": [
        "Specific genetic variant as genetic testing pending",
        "Optimal colchicine dose, may need titration based on clinical response and tolerability"
    ]
}
