{
    "primary_diagnosis": {
        "name": "Familial Mediterranean Fever (FMF)",
        "confidence": 0.7,
        "icd_code": "M30.0",
        "reasoning": "Recurrent episodes of fever, abdominal pain, and arthritis, especially in a young woman of Mediterranean descent, strongly suggest FMF. The self-limiting nature of the episodes and family history further support this diagnosis."
    },
    "differential_diagnoses": [
        {
            "name": "Acute Appendicitis",
            "confidence": 0.2,
            "icd_code": "K35.0",
            "reasoning": "Peritoneal signs and abdominal pain raise the possibility of appendicitis. However, the pattern of episodes and lack of response to analgesics are atypical."
        },
        {
            "name": "Inflammatory Bowel Disease (IBD)",
            "confidence": 0.1,
            "icd_code": "K50",
            "reasoning": "Abdominal pain and fever could be indicative of IBD. However, the absence of persistent diarrhea and the episodic nature of the symptoms make it less likely."
        },
        {
            "name": "Reactive Arthritis",
            "confidence": 0.1,
            "icd_code": "M06.0",
            "reasoning": "Arthritis following an infection could be considered. However, the patient's history does not suggest any recent infections."
        }
    ],
    "key_findings": [
        "Recurrent episodes of fever, abdominal pain, and arthritis",
        "Family history of similar episodes",
        "Positive peritoneal signs",
        "Elevated CRP and ESR"
    ],
    "diagnostic_tests": [
        {
            "test": "Genetic testing for MEFV gene mutations",
            "purpose": "Confirm diagnosis of FMF",
            "priority": "immediate"
        },
        {
            "test": "Complete Blood Count (CBC)",
            "purpose": "Rule out infection",
            "priority": "urgent"
        },
        {
            "test": "Stool studies (culture, ova and parasite)",
            "purpose": "Rule out infectious gastroenteritis",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Administer NSAIDs for pain and inflammation",
            "Provide intravenous fluids"
        ],
        "medications": [
            {
                "drug": "Colchicine",
                "dose": "0.6 mg orally three times a day",
                "route": "PO",
                "duration": "Indefinitely",
                "rationale": "First-line treatment for FMF to prevent and treat attacks"
            }
        ],
        "monitoring": [
            "Vital signs (temperature, blood pressure, heart rate)",
            "Pain levels",
            "Inflammatory markers (CRP, ESR)",
            "Kidney function",
            "Frequency and severity of attacks"
        ],
        "consultations": [
            {
                "specialty": "Rheumatologist",
                "urgency": "urgent",
                "reason": "For definitive diagnosis and management of FMF"
            }
        ]
    },
    "red_flags": [
        "Severe abdominal pain that does not improve with analgesics",
        "Signs of intestinal perforation"
    ],
    "patient_education": [
        "Explain the diagnosis of FMF and its potential complications",
        "Discuss the importance of taking colchicine consistently",
        "Provide information on managing and preventing attacks",
        "Emphasize the need for follow-up with a rheumatologist"
    ],
    "follow_up": {
        "timing": "Within 1 week for review of genetic test results and medication adjustments",
        "conditions": "Immediate return if symptoms worsen, develop fever above 39.5°C, or experience severe abdominal pain"
    },
    "confidence_in_assessment": 0.7,
    "uncertainties": [
        "Accuracy of genetic testing results",
        "Potential for other undiagnosed conditions contributing to symptoms"
    ]
}
