{
    "primary_diagnosis": {
        "name": "Familial Mediterranean Fever",
        "confidence": 0.8,
        "icd_code": "E85.0",
        "reasoning": "Recurrent episodes of fever, abdominal pain, and arthritis in a patient of Mediterranean descent, with family history of similar episodes, is highly suggestive of FMF."
    },
    "differential_diagnoses": [
        {
            "name": "Systemic Lupus Erythematosus",
            "confidence": 0.4,
            "icd_code": "M32",
            "reasoning": "SLE can present with similar episodic symptoms including fever, arthritis, and serositis. However, the recurrent nature and family history make FMF more likely."
        },
        {
            "name": "Acute Intermittent Porphyria",
            "confidence": 0.3,
            "icd_code": "E80.21",
            "reasoning": "AIP can cause recurrent abdominal pain episodes. However, fever and arthritis are not typical features."
        },
        {
            "name": "Hereditary Angioedema",
            "confidence": 0.2,
            "icd_code": "D84.1",
            "reasoning": "HAE can cause recurrent abdominal pain due to bowel edema. However, fever and arthritis would be unusual."
        }
    ],
    "key_findings": [
        "Recurrent self-limited episodes of fever, severe abdominal pain, and arthritis",
        "Family history of similar episodes",
        "Unresponsive to standard analgesics"
    ],
    "diagnostic_tests": [
        {
            "test": "MEFV gene mutation analysis",
            "purpose": "Confirm diagnosis of FMF",
            "priority": "urgent"
        },
        {
            "test": "CBC with differential",
            "purpose": "Evaluate for leukocytosis or anemia",
            "priority": "immediate"
        },
        {
            "test": "CRP and ESR",
            "purpose": "Assess inflammatory markers",
            "priority": "immediate"
        },
        {
            "test": "Urinalysis",
            "purpose": "Rule out UTI or proteinuria",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Admit for observation and symptom management",
            "Consult rheumatology and genetics",
            "Start empiric colchicine if high suspicion for FMF"
        ],
        "medications": [
            {
                "drug": "Colchicine",
                "dose": "0.6 mg PO BID",
                "route": "PO",
                "duration": "Lifelong",
                "rationale": "Mainstay prophylactic treatment for FMF"
            },
            {
                "drug": "Acetaminophen",
                "dose": "650 mg PO q6h PRN",
                "route": "PO",
                "duration": "As needed for fever/pain",
                "rationale": "Safer than NSAIDs given possible GI involvement"
            }
        ],
        "monitoring": [
            "Daily CRP/ESR",
            "I/O, vitals q4h"
        ],
        "consultations": [
            {
                "specialty": "Rheumatology",
                "urgency": "urgent",
                "reason": "Guide diagnosis and long-term management"
            },
            {
                "specialty": "Genetics",
                "urgency": "routine",
                "reason": "Counsel regarding inheritance, family testing"
            }
        ]
    },
    "red_flags": [
        "Worsening abdominal pain or peritoneal signs",
        "Hemodynamic instability",
        "Inability to tolerate PO"
    ],
    "patient_education": [
        "FMF is a genetic condition causing recurrent inflammatory episodes",
        "Lifelong treatment with colchicine can reduce frequency and severity of episodes",
        "Family members should be tested",
        "Discuss inheritance pattern and risk to future children"
    ],
    "follow_up": {
        "timing": "With rheumatology in 1-2 weeks, sooner if worsening",
        "conditions": "Fever >39°C, severe pain, vomiting, bleeding"
    },
    "confidence_in_assessment": 0.8,
    "uncertainties": [
        "Confirming diagnosis pending genetic testing",
        "Optimal colchicine dosing may require titration",
        "Ensuring medication access and adherence given language/cultural barriers"
    ]
}
