{
    "primary_diagnosis": {
        "name": "Delirium due to hypercalcemia (metabolic)",
        "confidence": 0.78,
        "icd_code": "F05.0",
        "reasoning": "Acute changes in cognition, hallucinations, weight loss, mild anemia and elevated calcium suggest a metabolic encephalopathy. Hypercalcemia is a known reversible cause of delirium in older adults."
    },
    "differential_diagnoses": [
        {
            "name": "Alzheimer's disease / mixed dementia",
            "confidence": 0.55,
            "icd_code": "G30.9",
            "reasoning": "Progressive cognitive decline and personality changes over months could represent early dementia; however, the rapid progression, weight loss, and hypercalcemia point more to a secondary cause."
        },
        {
            "name": "Major depressive disorder with psychotic features",
            "confidence": 0.40,
            "icd_code": "F33.3",
            "reasoning": "Depression in the elderly can present with fatigue, weight loss, and psychosis, but the presence of hypercalcemia and falls makes a primary psychiatric diagnosis less likely."
        },
        {
            "name": "Multiple myeloma (or other malignancy causing hypercalcemia)",
            "confidence": 0.65,
            "icd_code": "C90.0",
            "reasoning": "Weight loss, anemia, hypercalcemia, and new neuropsychiatric symptoms are classic for a plasma‑cell disorder or other malignancy producing paraneoplastic hypercalcemia."
        }
    ],
    "key_findings": [
        "Progressive fatigue, 15‑lb weight loss, and intermittent confusion over 6 months",
        "Mild anemia, mildly elevated serum calcium, recent falls, hallucinations (talking to deceased husband)"
    ],
    "diagnostic_tests": [
        {
            "test": "Comprehensive metabolic panel with ionized calcium",
            "purpose": "Confirm degree of hypercalcemia, assess renal function and electrolyte disturbances",
            "priority": "immediate"
        },
        {
            "test": "Serum parathyroid hormone (PTH) level",
            "purpose": "Distinguish primary hyperparathyroidism from malignancy‑related hypercalcemia",
            "priority": "urgent"
        },
        {
            "test": "Serum protein electrophoresis (SPEP) with immunofixation",
            "purpose": "Screen for multiple myeloma or other monoclonal gammopathies",
            "priority": "urgent"
        },
        {
            "test": "CT chest/abdomen/pelvis with contrast",
            "purpose": "Identify possible malignancy (lung, breast, renal) causing hypercalcemia",
            "priority": "urgent"
        },
        {
            "test": "Brain MRI without contrast",
            "purpose": "Rule out structural brain lesions contributing to delirium",
            "priority": "routine"
        },
        {
            "test": "Complete blood count with differential",
            "purpose": "Assess anemia severity and look for leukocytosis or other abnormalities",
            "priority": "immediate"
        },
        {
            "test": "Thyroid function tests (TSH, free T4)",
            "purpose": "Exclude hypothyroidism as a contributor to cognitive change",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Initiate aggressive IV hydration with isotonic saline (2–3 L/24 h) to promote calciuresis",
            "Begin IV bisphosphonate (e.g., zoledronic acid 4 mg IV over 15 min) after confirming renal function",
            "Obtain ECG to assess for QT prolongation or arrhythmias"
        ],
        "medications": [
            {
                "drug": "Zozo (Zoledronic acid)",
                "dose": "4 mg IV single dose",
                "route": "IV",
                "duration": "Single dose; repeat if calcium remains >12 mg/dL after 48 h",
                "rationale": "Inhibits osteoclast-mediated bone resorption, rapidly lowering calcium"
            },
            {
                "drug": "Calcitriol (active vitamin D)",
                "dose": "0.25 µg PO daily",
                "route": "PO",
                "duration": "Until calcium normalizes; then reassess",
                "rationale": "Used if PTH‑mediated hypercalcemia is confirmed"
            },
            {
                "drug": "Ceftriaxone",
                "dose": "1 g IV q24h",
                "route": "IV",
                "duration": "5‑7 days if infection suspected",
                "rationale": "Empiric coverage for urinary or pulmonary infection that could precipitate delirium"
            }
        ],
        "monitoring": [
            "Serum calcium and creatinine q12 h during initial therapy",
            "Mental status exam q8 h",
            "Fluid balance and urine output",
            "ECG for QT changes daily"
        ],
        "consultations": [
            {
                "specialty": "Geriatric Medicine",
                "urgency": "immediate",
                "reason": "Comprehensive assessment of functional status, falls risk, and medication review"
            },
            {
                "specialty": "Endocrinology",
                "urgency": "urgent",
                "reason": "Management of hypercalcemia and evaluation for primary hyperparathyroidism"
            },
            {
                "specialty": "Oncology/Hematology",
                "urgency": "urgent",
                "reason": "Evaluate for multiple myeloma or other malignancy"
            },
            {
                "specialty": "Psychiatry",
                "urgency": "routine",
                "reason": "Assess for psychosis, depression, and need for antipsychotic if agitation persists"
            }
        ]
    },
    "red_flags": [
        "Serum calcium >13 mg/dL (risk of cardiac arrhythmia, pancreatitis)",
        "Rapid worsening of mental status or seizure activity",
        "New onset atrial fibrillation or prolonged QT interval"
    ],
    "patient_education": [
        "Explain that high calcium levels can cause confusion and hallucinations, but they are treatable.",
        "Emphasize importance of hydration and medication adherence to lower calcium."
    ],
    "follow_up": {
        "timing": "Re‑evaluate in 48 hours for calcium trend and mental status; outpatient follow‑up in 1 week if stable.",
        "conditions": "Return immediately if new seizures, severe weakness, chest pain, or worsening confusion."
    },
    "confidence_in_assessment": 0.78,
    "uncertainties": [
        "Exact etiology of hypercalcemia (primary hyperparathyroidism vs. malignancy) pending labs",
        "Extent of underlying neurodegenerative disease versus reversible metabolic cause"
    ]
}
