{
    "primary_diagnosis": {
        "name": "Hypertensive encephalopathy",
        "confidence": 0.75,
        "icd_code": "I67.4",
        "reasoning": "Severely elevated BP (190/110) with classic triad of headaches, visual disturbances, and altered mental status. Paranoid ideation and tactile hallucinations can manifest as neurological symptoms in hypertensive encephalopathy."
    },
    "differential_diagnoses": [
        {
            "name": "Delayed traumatic intracranial hemorrhage",
            "confidence": 0.65,
            "icd_code": "S06.6",
            "reasoning": "Head trauma 2 weeks ago with delayed onset of neurological symptoms including headaches and altered mental status. Delayed hemorrhage can occur days to weeks after initial trauma."
        },
        {
            "name": "Methamphetamine-induced psychosis",
            "confidence": 0.45,
            "icd_code": "F15.15",
            "reasoning": "History of methamphetamine use with classic paranoid ideation and tactile hallucinations ('bugs crawling under skin'). Symptoms can persist for months after cessation."
        },
        {
            "name": "Primary delusional parasitosis",
            "confidence": 0.35,
            "icd_code": "F22",
            "reasoning": "Classic presentation of feeling bugs crawling under skin with extensive scratching marks. However, doesn't explain hypertension and other neurological symptoms."
        }
    ],
    "key_findings": [
        "Severely elevated blood pressure (190/110)",
        "Recent head trauma with delayed neurological symptoms",
        "Combination of headaches, visual disturbances, and altered mental status",
        "Tactile hallucinations with self-injurious scratching behavior"
    ],
    "diagnostic_tests": [
        {
            "test": "Non-contrast CT head",
            "purpose": "Rule out delayed intracranial hemorrhage or mass lesion",
            "priority": "immediate"
        },
        {
            "test": "Basic metabolic panel and CBC",
            "purpose": "Assess for metabolic causes and coagulopathy",
            "priority": "immediate"
        },
        {
            "test": "Urine toxicology screen",
            "purpose": "Confirm reported sobriety and rule out recent substance use",
            "priority": "urgent"
        },
        {
            "test": "ECG",
            "purpose": "Assess for cardiac effects of hypertension",
            "priority": "urgent"
        },
        {
            "test": "Ophthalmologic examination",
            "purpose": "Evaluate for papilledema and hypertensive retinopathy",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Continuous cardiac and neurological monitoring",
            "IV access and prepare for controlled blood pressure reduction",
            "Protect patient from self-injury due to scratching behavior",
            "Contact neurology for urgent consultation"
        ],
        "medications": [
            {
                "drug": "Nicardipine IV",
                "dose": "5mg/hr initial, titrate to effect",
                "route": "IV",
                "duration": "Until BP controlled",
                "rationale": "First-line agent for hypertensive emergency with good CNS penetration"
            },
            {
                "drug": "Haloperidol",
                "dose": "2.5-5mg",
                "route": "IM",
                "duration": "PRN agitation",
                "rationale": "Control agitation and psychotic symptoms if needed after medical stabilization"
            }
        ],
        "monitoring": [
            "Blood pressure every 15 minutes initially",
            "Neurological checks every 30 minutes",
            "Continuous cardiac monitoring",
            "Hourly urine output"
        ],
        "consultations": [
            {
                "specialty": "Neurology",
                "urgency": "immediate",
                "reason": "Evaluate for hypertensive encephalopathy vs delayed TBI complications"
            },
            {
                "specialty": "Neurosurgery",
                "urgency": "urgent",
                "reason": "Assess need for intervention if intracranial hemorrhage identified"
            },
            {
                "specialty": "Psychiatry",
                "urgency": "routine",
                "reason": "Address psychotic symptoms and substance use history after medical stabilization"
            }
        ]
    },
    "red_flags": [
        "Rapidly worsening neurological status",
        "Signs of increased intracranial pressure",
        "Seizure activity",
        "Further blood pressure elevation above 200/120"
    ],
    "patient_education": [
        "Explain that medical conditions need to be ruled out before attributing symptoms to substance use",
        "Importance of blood pressure control and medication compliance",
        "Need for close follow-up and housing stability for medication management"
    ],
    "follow_up": {
        "timing": "24-48 hours if discharged, or until medically stable if admitted",
        "conditions": "Return immediately for worsening headache, vision changes, confusion, or seizures"
    },
    "confidence_in_assessment": 0.70,
    "uncertainties": [
        "Difficulty distinguishing between hypertensive encephalopathy and delayed TBI complications",
        "Unclear reliability of substance use history and timeline",
        "Limited social history and ability to ensure medication compliance",
        "Potential for multiple overlapping conditions"
    ]
}
