    "primary_diagnosis": {
        "name": "Hypertensive encephalopathy",
        "confidence": 0.75,
        "icd_code": "I67.4",
        "reasoning": "Severe hypertension (190/110) with headaches and visual disturbances, combined with recent head trauma, suggests possible encephalopathy; need to rule out medical causes before anchoring on psychiatric or substance-related issues."
    },
    "differential_diagnoses": [
        {
            "name": "Methamphetamine-induced psychotic disorder",
            "confidence": 0.6,
            "icd_code": "F15.959",
            "reasoning": "History of meth use, paranoid ideation, and formication (bugs under skin) with scratching marks align with substance-induced psychosis, though patient claims sobriety."
        },
        {
            "name": "Chronic subdural hematoma",
            "confidence": 0.7,
            "icd_code": "I62.00",
            "reasoning": "Recent head trauma 2 weeks ago, with new onset headaches, visual disturbances, and altered mental status; delayed presentation possible in homeless population with poor access to care."
        },
        {
            "name": "Delirium due to multiple etiologies",
            "confidence": 0.5,
            "icd_code": "F05",
            "reasoning": "Combination of possible substance withdrawal, hypertension, and trauma could precipitate delirium; socioeconomic factors like homelessness may contribute to malnutrition or infection."
        }
    ],
    "key_findings": [
        "Severe hypertension (BP 190/110)",
        "Recent head trauma with headaches and visual disturbances",
        "Paranoid ideation and formication with scratching marks",
        "History of methamphetamine use and homelessness"
    ],
    "diagnostic_tests": [
        {
            "test": "Non-contrast CT head",
            "purpose": "Rule out intracranial hemorrhage or subdural hematoma",
            "priority": "immediate"
        },
        {
            "test": "Urine toxicology screen",
            "purpose": "Confirm or rule out recent methamphetamine use",
            "priority": "immediate"
        },
        {
            "test": "Basic metabolic panel (BMP) and CBC",
            "purpose": "Assess for electrolyte imbalances, infection, or renal involvement in hypertension",
            "priority": "urgent"
        },
        {
            "test": "Fundoscopic exam or ophthalmology evaluation",
            "purpose": "Evaluate for papilledema or hypertensive retinopathy causing visual disturbances",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Admit to emergency department for blood pressure control and monitoring",
            "Initiate IV access and continuous BP monitoring"
        ],
        "medications": [
            {
                "drug": "Nicardipine",
                "dose": "5-15 mg/hr",
                "route": "IV",
                "duration": "Titrate to BP goal, then transition to oral",
                "rationale": "Rapidly reduce blood pressure in hypertensive emergency to prevent end-organ damage"
            },
            {
                "drug": "Lorazepam",
                "dose": "1-2 mg",
                "route": "IV",
                "duration": "As needed for agitation",
                "rationale": "Manage acute agitation from paranoia or formication if not controlled by environment"
            }
        ],
        "monitoring": [
            "Blood pressure and neurologic status",
            "Every 15-30 minutes initially, then hourly"
        ],
        "consultations": [
            {
                "specialty": "Neurology",
                "urgency": "immediate",
                "reason": "Evaluate for encephalopathy, trauma sequelae, or need for further imaging"
            },
            {
                "specialty": "Psychiatry",
                "urgency": "urgent",
                "reason": "Assess for substance-induced vs primary psychiatric disorder once medical stability achieved"
            },
            {
                "specialty": "Social services",
                "urgency": "routine",
                "reason": "Address homelessness and barriers to care, considering socioeconomic factors"
            }
        ]
    },
    "red_flags": [
        "Severe hypertension with end-organ symptoms (headaches, visual changes)",
        "Recent head trauma with delayed neurologic symptoms",
        "Altered mental status in context of substance use history"
    ],
    "patient_education": [
        "Avoid illicit substances to prevent worsening symptoms and hypertension; discuss risks of meth use especially in homeless individuals with limited support",
        "Importance of blood pressure control and seeking regular healthcare despite socioeconomic challenges; provide resources for free clinics"
    ],
    "follow_up": {
        "timing": "Within 1 week post-discharge or sooner if symptoms worsen",
        "conditions": "Immediate return for worsening headaches, vision changes, seizures, or uncontrolled paranoia"
    },
    "confidence_in_assessment": 0.65,
    "uncertainties": [
        "Accuracy of patient's reported sobriety duration and substance use history",
        "Details of head trauma (mechanism, severity) and potential for unreported injuries",
        "Impact of homelessness on nutrition, infections, or other comorbidities not yet identified"
    ]
}
