        },
        {
            "name": "Periodic Paralysis Disorders",
            "confidence": 0.6,
            "icd_code": "E83.3",
            "reasoning": "Intermittent muscle weakness, especially with a history of consanguinity, can be indicative of channelopathies."
        },
        {
            "name": "Mitochondrial Disease",
            "confidence": 0.55,
            "icd_code": "G73.2",
            "reasoning": "Exercise intolerance and potential for dark urine may indicate mitochondrial dysfunction."
        }
    ],
    "key_findings": [
        "Exercise intolerance",
        "Elevated CK levels during episodes"
    ],
    "diagnostic_tests": [
        {
            "test": "CK levels monitoring",
            "purpose": "Confirm elevated CK and monitor response to treatment",
            "priority": "immediate"
        },
        {
            "test": "Muscle biopsy",
            "purpose": "Differentiate between primary muscle disorders and rhabdomyolysis",
            "priority": "urgent"
        },
        {
            "test": "Metabolic panel",
            "purpose": "Check for metabolic abnormalities indicative of McArdle's or mitochondrial disease",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Hydration: Encourage adequate fluid intake to prevent renal complications.",
            "Rest: Recommend complete physical rest until CK levels normalize."
        ],
        "medications": [
            {
                "drug": "Sodium bicarbonate",
                "dose": "1-2 teaspoons (5-10mmol) in water",
                "route": "PO",
                "duration": "Until CK levels normalize",
                "rationale": "Alkalize urine to prevent myoglobinuria and kidney damage."
            }
        ],
        "monitoring": [
            "Continuously monitor CK levels until normalized.",
            "Renal function tests to check for any kidney damage."
        ],
        "consultations": [
            {
                "specialty": "Clinical Genetics",
                "urgency": "routine",
                "reason": "Confirm potential genetic disorder and guide further investigations."
            },
            {
                "specialty": "Pediatric Neurology",
                "urgency": "urgent",
                "reason": "Assist in differentiating between various muscle and neurological disorders."
            }
        ]
    },
    "red_flags": [
        "Prolonged and severe muscle pain",
        "Significant elevation of CK levels (>10,000)"
    ],
    "patient_education": [
        "Educate on the importance of fluid intake before, during, and after intense exercise.",
        "Explain the potential genetic basis and need for genetic counseling."
    ],
    "follow_up": {
        "timing": "2-3 days after discharge for CK level re-evaluation",
        "conditions": "Return immediately if new symptoms, especially worsening muscle weakness or pain."
    },
