{
    "primary_diagnosis": {
        "name": "McArdle disease (Glycogen storage disease type V)",
        "confidence": 0.88,
        "icd_code": "E74.0",
        "reasoning": "Adolescent with exercise intolerance, episodic muscle pain, myoglobinuria, and CK spikes after intense aerobic activity; consanguineous parents support autosomal recessive inheritance; classic presentation for PYGM deficiency."
    },
    "differential_diagnoses": [
        {
            "name": "Carnitine palmitoyltransferase II deficiency (CPT2 deficiency)",
            "confidence": 0.45,
            "icd_code": "E71.3",
            "reasoning": "Also presents with exercise‑induced rhabdomyolysis, dark urine, and CK elevation; triggered by prolonged aerobic effort; autosomal recessive and common in consanguineous families."
        },
        {
            "name": "Pompe disease (Glycogen storage disease type II)",
            "confidence": 0.30,
            "icd_code": "E74.0",
            "reasoning": "Another glycogen storage disorder with muscle weakness and possible CK elevation; usually presents earlier with cardiomyopathy, but late‑onset forms can mimic exercise intolerance."
        },
        {
            "name": "Inflammatory myopathy (e.g., polymyositis)",
            "confidence": 0.20,
            "icd_code": "M33.2",
            "reasoning": "Can cause muscle weakness and CK rise, but typically lacks the clear exercise‑triggered pattern and dark urine; less likely given age and episodic nature."
        }
    ],
    "key_findings": [
        "Exercise intolerance with severe muscle pain after intense swimming",
        "Intermittent dark urine (myoglobinuria) post‑exercise",
        "CK levels up to 5,000 IU/L during episodes, normal at baseline",
        "Consanguineous parents (first cousins)"
    ],
    "diagnostic_tests": [
        {
            "test": "Forearm ischemic exercise test (non‑ischemic protocol)",
            "purpose": "Assess lactate and ammonia response; flat lactate rise suggests McArdle disease",
            "priority": "urgent"
        },
        {
            "test": "Genetic testing for PYGM gene mutations",
            "purpose": "Confirm diagnosis of McArdle disease",
            "priority": "routine"
        },
        {
            "test": "Acylcarnitine profile and plasma free fatty acids",
            "purpose": "Rule out fatty‑acid oxidation defects such as CPT2 deficiency",
            "priority": "routine"
        },
        {
            "test": "Muscle biopsy with myophosphorylase activity assay",
            "purpose": "Direct enzymatic confirmation if genetic testing delayed",
            "priority": "routine"
        },
        {
            "test": "Serum creatinine, BUN, electrolytes, and urinalysis for myoglobin",
            "purpose": "Assess renal involvement during acute rhabdomyolysis",
            "priority": "immediate"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Aggressive oral/IV hydration (goal urine output >200 mL/h) during acute episodes",
            "Monitor renal function and CK every 12 hours until trending down"
        ],
        "medications": [],
        "monitoring": [
            "CK level – check daily during acute episode, then weekly for 1 month",
            "Renal function (creatinine, electrolytes) – daily during episode, then monthly"
        ],
        "consultations": [
            {
                "specialty": "Neurology",
                "urgency": "immediate",
                "reason": "Confirm diagnosis, coordinate metabolic work‑up, guide activity plan"
            },
            {
                "specialty": "Medical Genetics",
                "urgency": "routine",
                "reason": "Genetic counseling for patient and family, arrange confirmatory testing"
            },
            {
                "specialty": "Nephrology",
                "urgency": "urgent",
                "reason": "Assess and manage potential acute kidney injury from rhabdomyolysis"
            }
        ]
    },
    "red_flags": [
        "Progressive rise in serum creatinine indicating acute kidney injury",
        "Persistent or worsening muscle pain with swelling suggestive of compartment syndrome"
