{
    "primary_diagnosis": {
        "name": "McArdle disease (Glycogen Storage Disease Type V)",
        "confidence": 0.85,
        "icd_code": "E74.04",
        "reasoning": "Exercise intolerance with myalgia, myoglobinuria after intense exercise, elevated CK during episodes but normal between, autosomal recessive inheritance pattern from consanguineous parents, and typical adolescent presentation all strongly suggest McArdle disease"
    },
    "differential_diagnoses": [
        {
            "name": "Carnitine palmitoyltransferase II deficiency",
            "confidence": 0.15,
            "icd_code": "E71.3",
            "reasoning": "Also presents with exercise-induced rhabdomyolysis and myoglobinuria, autosomal recessive inheritance, but typically triggered by prolonged exercise rather than high-intensity bursts"
        },
        {
            "name": "Mitochondrial myopathy",
            "confidence": 0.10,
            "icd_code": "G73.6",
            "reasoning": "Can cause exercise intolerance and muscle weakness, but typically affects prolonged low-intensity exercise more than high-intensity bursts"
        },
        {
            "name": "Glycogen Storage Disease Type VII (Tarui disease)",
            "confidence": 0.08,
            "icd_code": "E74.09",
            "reasoning": "Similar presentation to McArdle disease with exercise intolerance and myoglobinuria, but much rarer"
        }
    ],
    "key_findings": [
        "Exercise-induced myoglobinuria in competitive swimmer",
        "CK elevation during episodes but normal between episodes",
        "Consanguineous parents suggesting autosomal recessive inheritance",
        "Exercise intolerance with muscle pain triggered by high-intensity swimming"
    ],
    "diagnostic_tests": [
        {
            "test": "PYGM gene sequencing",
            "purpose": "Confirm McArdle disease diagnosis",
            "priority": "urgent"
        },
        {
            "test": "Plasma acylcarnitine profile",
            "purpose": "Rule out fatty acid oxidation defects like CPT II deficiency",
            "priority": "urgent"
        },
        {
            "test": "Forearm exercise test (non-ischemic)",
            "purpose": "Assess lactate and ammonia response to exercise",
            "priority": "urgent"
        },
        {
            "test": "CPT2 gene sequencing",
            "purpose": "Rule out carnitine palmitoyltransferase II deficiency",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Temporarily restrict high-intensity swimming training",
            "Ensure adequate hydration during any exercise",
            "Monitor for signs of acute kidney injury during episodes"
        ],
        "medications": [
            {
                "drug": "Sucrose/glucose",
                "dose": "15-30g oral",
                "route": "PO",
                "duration": "Before exercise as needed",
                "rationale": "Pre-exercise carbohydrate loading can improve exercise tolerance in McArdle disease"
            }
        ],
        "monitoring": [
            "CK levels during and after exercise episodes",
            "Urine color and myoglobin levels",
            "Renal function if myoglobinuria occurs"
        ],
        "consultations": [
            {
                "specialty": "Neuromuscular specialist",
                "urgency": "urgent",
                "reason": "Expert evaluation and management of suspected metabolic myopathy"
            },
            {
                "specialty": "Genetic counselor",
                "urgency": "routine",
                "reason": "Family counseling regarding autosomal recessive inheritance pattern"
            },
            {
                "specialty": "Sports medicine physician",
                "urgency": "routine",
                "reason": "Safe exercise planning for competitive swimmer with metabolic myopathy"
            }
        ]
    },
    "red_flags": [
        "Acute kidney injury from severe rhabdomyolysis",
        "Persistent muscle weakness between episodes",
        "Cardiac involvement or arrhythmias during episodes"
    ],
    "patient_education": [
        "Avoid high-intensity exercise bursts until diagnosis confirmed",
        "Understand the importance of gradual warm-up and pre-exercise nutrition",
        "Learn to recognize early signs of muscle breakdown (dark urine, severe muscle pain)",
        "Discuss genetic inheritance patterns and family planning implications"
    ],
    "follow_up": {
        "timing": "Within 2 weeks for test results and specialist appointment",
        "conditions": "Return immediately for dark urine, severe muscle pain lasting >24 hours, or any signs of kidney problems"
