{
    "primary_diagnosis": {
        "name": "McArdle disease (Glycogen storage disease type V)",
        "confidence": 0.85,
        "icd_code": "E74.04",
        "reasoning": "Episodic exercise-induced muscle weakness, pain, and dark urine (myoglobinuria) with elevated CK, in context of consanguineous parents, strongly suggest this autosomal recessive metabolic myopathy."
    },
    "differential_diagnoses": [
        {
            "name": "Carnitine palmitoyltransferase II deficiency",
            "confidence": 0.6,
            "icd_code": "E71.314",
            "reasoning": "Presents with exercise-induced rhabdomyolysis and myoglobinuria, often in adolescents; genetic inheritance fits consanguinity."
        },
        {
            "name": "Tarui disease (Glycogen storage disease type VII)",
            "confidence": 0.5,
            "icd_code": "E74.09",
            "reasoning": "Similar exercise intolerance and muscle symptoms due to glycolytic defect; autosomal recessive, consistent with family history."
        },
        {
            "name": "Very long-chain acyl-CoA dehydrogenase deficiency",
            "confidence": 0.4,
            "icd_code": "E71.310",
            "reasoning": "Can cause rhabdomyolysis triggered by exercise or fasting; genetic and episodic nature aligns, though less common in this presentation."
        }
    ],
    "key_findings": [
        "Exercise-induced muscle weakness and pain with dark urine (myoglobinuria)",
        "Elevated CK to 5,000 during episodes, normal interictally",
        "Consanguineous parents suggesting autosomal recessive inheritance",
        "Adolescent competitive athlete with episodic symptoms"
    ],
    "diagnostic_tests": [
        {
            "test": "Serum electrolytes, BUN, creatinine, urinalysis",
            "purpose": "Assess for rhabdomyolysis complications like acute kidney injury",
            "priority": "immediate"
        },
        {
            "test": "Genetic testing for metabolic myopathies (e.g., PYGM gene for McArdle)",
            "purpose": "Confirm specific genetic diagnosis",
            "priority": "urgent"
        },
        {
            "test": "Muscle biopsy with enzyme histochemistry",
            "purpose": "Evaluate for glycogen accumulation or enzyme deficiencies",
            "priority": "urgent"
        },
        {
            "test": "Forearm ischemic exercise test",
            "purpose": "Assess for lack of lactate rise indicative of glycolytic defects",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Intravenous hydration to prevent renal complications from rhabdomyolysis",
            "Activity restriction: avoid intense exercise until diagnosis confirmed"
        ],
        "medications": [
            {
                "drug": "Normal saline",
                "dose": "1-2 L bolus followed by 200-300 mL/hr",
                "route": "IV",
                "duration": "Until CK normalizes and urine output adequate",
                "rationale": "To promote myoglobin clearance and prevent acute kidney injury"
            },
            {
                "drug": "Acetaminophen",
                "dose": "650 mg every 6 hours as needed",
                "route": "PO",
                "duration": "Short-term for symptom relief",
                "rationale": "For muscle pain management without risking further muscle damage"
            }
        ],
        "monitoring": [
            "CK levels, renal function, urine output",
            "Daily during acute episode, then weekly"
        ],
        "consultations": [
            {
                "specialty": "Pediatric Neurology",
                "urgency": "urgent",
                "reason": "Expertise in metabolic myopathies and muscle disorders in adolescents"
            },
            {
                "specialty": "Genetics",
                "urgency": "routine",
                "reason": "Counseling and testing for autosomal recessive disorders given consanguinity"
            },
            {
                "specialty": "Pediatric Metabolism",
                "urgency": "urgent",
                "reason": "Specialized evaluation of glycogen storage diseases"
            }
        ]
    },
    "red_flags": [
        "Worsening renal function or oliguria indicating acute kidney injury",
        "Recurrent severe weakness or dark urine suggesting ongoing rhabdomyolysis",
        "Family history of similar symptoms or sudden death"
    ],
    "patient_education": [
        "Avoid intense physical activity and triggers like fasting to prevent episodes; consider pre-exercise carbohydrate loading if McArdle confirmed",
        "Recognize early signs of rhabdomyolysis (dark urine, severe pain) and seek immediate care; discuss genetic implications with family due to consanguinity"
    ],
    "follow_up": {
        "timing": "1-2 weeks after acute episode, then every 3 months",
        "conditions": "Immediate return for dark urine, severe muscle pain, weakness, or decreased urine output"
    },
    "confidence_in_assessment": 0.8,
    "uncertainties": [
        "Exact genetic mutation requires confirmation testing",
        "Potential overlap with other metabolic defects; social isolation (homeschooling) may indicate additional psychosocial factors needing exploration"
    ]
}
