{
    "primary_diagnosis": {
        "name": "Idiopathic Parkinson disease",
        "confidence": 0.72,
        "icd_code": "G20",
        "reasoning": "Classic triad of resting tremor, bradykinesia, and rigidity with a subacute onset over months; basal ganglia MRI is often normal or shows subtle nonspecific changes. Lack of exposure timeline strongly supporting a primary neurodegenerative process."
    },
    "differential_diagnoses": [
        {
            "name": "Manganese-induced parkinsonism (secondary parkinsonism)",
            "confidence": 0.45,
            "icd_code": "G21.2",
            "reasoning": "Work in water purification in Bangladesh may involve exposure to manganese or other metals; basal ganglia signal changes can be seen. Symptoms overlap but often have less resting tremor and more gait disturbance."
        },
        {
            "name": "Wilson disease (copper accumulation)",
            "confidence": 0.30,
            "icd_code": "E83.0",
            "reasoning": "Can present with movement disorder and basal ganglia MRI abnormalities in adults; however, hepatic symptoms and younger typical onset make it less likely."
        },
        {
            "name": "Drug‑induced parkinsonism",
            "confidence": 0.25,
            "icd_code": "G21.1",
            "reasoning": "Consider if patient used dopamine‑blocking agents (e.g., antipsychotics) during the overseas assignment; history not provided but must be ruled out."
        }
    ],
    "key_findings": [
        "Progressive resting tremor, bradykinesia, rigidity over 3 months",
        "Subtle basal ganglia signal abnormalities on MRI"
    ],
    "diagnostic_tests": [
        {
            "test": "Serum manganese level",
            "purpose": "Rule in/out manganese toxicity as cause of secondary parkinsonism",
            "priority": "urgent"
        },
        {
            "test": "Serum ceruloplasmin, copper, 24‑hour urinary copper",
            "purpose": "Exclude Wilson disease",
            "priority": "urgent"
        },
        {
            "test": "DaTscan (123I‑FP‑CIT SPECT)",
            "purpose": "Differentiate neurodegenerative Parkinson disease from drug‑induced or functional causes",
            "priority": "routine"
        },
        {
            "test": "Comprehensive medication review and toxicology screen",
            "purpose": "Identify possible dopamine‑blocking agents or other neurotoxic exposures",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Refer to movement‑disorder neurologist for confirmation and initiation of therapy",
            "Order urgent serum manganese and copper studies"
        ],
        "medications": [
            {
                "drug": "Levodopa/Carbidopa",
                "dose": "25/100 mg PO three times daily",
                "route": "PO",
                "duration": "Start now, titrate based on response",
                "rationale": "First‑line symptomatic treatment for Parkinson disease; improves bradykinesia and rigidity"
            },
            {
                "drug": "Pramipexole",
                "dose": "0.125 mg PO once daily, titrate up to 1.5 mg/day as needed",
                "route": "PO",
                "duration": "Adjunct if levodopa insufficient or for tremor control",
                "rationale": "Dopamine agonist useful in early disease and may reduce levodopa dose"
            }
        ],
        "monitoring": [
            "Motor symptom severity (UPDRS) – every 3 months",
            "Medication side effects (nausea, orthostatic hypotension, dyskinesia) – each visit",
            "Serum manganese and copper levels if initially abnormal – repeat in 3 months"
        ],
        "consultations": [
            {
                "specialty": "Neurology – movement disorders",
                "urgency": "immediate",
                "reason": "Confirm diagnosis, start disease‑specific therapy, arrange DaTscan"
            },
            {
                "specialty": "Occupational medicine / Toxicology",
                "urgency": "urgent",
                "reason": "Evaluate possible environmental metal exposure and advise on remediation"
            }
        ]
    },
    "red_flags": [
        "Rapid progression of symptoms within weeks",
        "Development of gait instability, falls, or early cognitive decline",
        "New onset visual hallucinations or psychosis"
    ],
    "patient_education": [
        "Parkinson disease is a progressive neurodegenerative disorder; medications can markedly improve quality of life.",
        "Environmental exposures can mimic or worsen symptoms, but treatment focuses on the underlying neurobiology; unnecessary avoidance of all exposures without evidence may cause anxiety."
    ],
    "follow_up": {
        "timing": "Neurology appointment within 2 weeks; primary care follow‑up in 4–6 weeks",
        "conditions": "Worsening tremor, new gait instability, severe medication side effects, or abnormal toxin labs require immediate return"
    },
    "confidence_in_assessment": 0.78,
    "uncertainties": [
        "Exact level and duration of manganese or other metal exposure during overseas assignment",
        "Potential undisclosed use of dopamine‑blocking medications while abroad"
    ]
}
