{
    "primary_diagnosis": {
        "name": "Manganese-induced parkinsonism (Manganism)",
        "confidence": 0.85,
        "icd_code": "T57.2",
        "reasoning": "Strong occupational exposure history in Bangladesh (water purification work), classic triad of parkinsonian symptoms, MRI basal ganglia changes consistent with manganese deposition, appropriate timeline with symptom onset after exposure period"
    },
    "differential_diagnoses": [
        {
            "name": "Idiopathic Parkinson's Disease",
            "confidence": 0.25,
            "icd_code": "G20",
            "reasoning": "Similar symptom presentation, but young age (45), bilateral onset suggested by MRI changes, and occupational exposure history make this less likely"
        },
        {
            "name": "Mixed heavy metal toxicity",
            "confidence": 0.40,
            "icd_code": "T56.9",
            "reasoning": "Bangladesh has high levels of arsenic and other metal contamination in water; mixed exposure could cause neurological symptoms"
        },
        {
            "name": "Wilson's Disease",
            "confidence": 0.15,
            "icd_code": "E83.01",
            "reasoning": "Can cause movement disorders and basal ganglia changes, but less likely given age and clear exposure history"
        }
    ],
    "key_findings": [
        "Progressive parkinsonian symptoms (tremor, bradykinesia, rigidity)",
        "Two-year occupational exposure in rural Bangladesh water purification",
        "MRI basal ganglia signal abnormalities",
        "Symptoms onset 6 months after return from exposure",
        "No family history of movement disorders"
    ],
    "diagnostic_tests": [
        {
            "test": "Serum manganese level",
            "purpose": "Confirm manganese exposure/toxicity",
            "priority": "immediate"
        },
        {
            "test": "DaTscan (Dopamine transporter scan)",
            "purpose": "Differentiate manganism (normal) from Parkinson's disease (abnormal)",
            "priority": "urgent"
        },
        {
            "test": "Comprehensive heavy metals panel (blood and urine)",
            "purpose": "Screen for arsenic, lead, mercury, and other toxic metals",
            "priority": "urgent"
        },
        {
            "test": "Detailed brain MRI with basal ganglia sequences",
            "purpose": "Assess for T1 hyperintensity in globus pallidus characteristic of manganese deposition",
            "priority": "urgent"
        },
        {
            "test": "Liver function tests and ceruloplasmin",
            "purpose": "Rule out Wilson's disease and assess for hepatic dysfunction",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Ensure complete cessation of any ongoing manganese exposure",
            "Obtain detailed occupational and environmental exposure history",
            "Assess functional status and safety (fall risk evaluation)"
        ],
        "medications": [
            {
                "drug": "Levodopa/Carbidopa",
                "dose": "25/100 mg TID",
                "route": "PO",
                "duration": "Trial for 4-6 weeks",
                "rationale": "Symptomatic treatment, though response often poor in manganism compared to IPD"
            }
        ],
        "monitoring": [
            "Serial manganese levels if initially elevated",
            "Motor function assessment using UPDRS scale",
            "Cognitive assessment for potential non-motor symptoms"
        ],
        "consultations": [
            {
                "specialty": "Movement Disorders Neurology",
                "urgency": "urgent",
                "reason": "Expert evaluation of parkinsonian syndrome and confirmation of diagnosis"
            },
            {
                "specialty": "Occupational Medicine",
                "urgency": "routine",
                "reason": "Assessment of workplace safety and exposure prevention"
            }
        ]
    },
    "red_flags": [
        "Rapid progression of symptoms",
        "Development of dystonia or 'cock walk' gait pattern",
        "Cognitive decline or psychiatric symptoms",
        "Poor response to levodopa therapy"
    ],
    "patient_education": [
        "Manganism is caused by manganese exposure and differs from Parkinson's disease",
        "Complete avoidance of manganese exposure is crucial",
        "Symptoms may stabilize but rarely improve significantly after exposure cessation",
        "Regular neurological follow-up is important for symptom monitoring"
    ],
    "follow_up": {
        "timing": "2 weeks for test results review, then monthly for first 3 months",
        "conditions": "Worsening motor symptoms, falls, development of swallowing difficulties, or new neurological symptoms"
    },
    "confidence_in_assessment": 0.80,
    "uncertainties": [
        "Exact duration and level of manganese exposure during Bangladesh assignment",
        "Whether symptoms will progress despite cessation of exposure",
        "Potential for recovery or symptom stabilization",
        "Possible concurrent exposure to other neurotoxic metals"
    ]
}
