{
    "primary_diagnosis": {
        "name": "Secondary parkinsonism due to environmental toxin exposure (possible manganese toxicity)",
        "confidence": 0.75,
        "icd_code": "G21.2",
        "reasoning": "Symptoms of tremor, bradykinesia, and rigidity with basal ganglia MRI changes, combined with occupational exposure in rural Bangladesh water projects, suggest toxin-induced parkinsonism; temporal delay post-exposure fits manganism."
    },
    "differential_diagnoses": [
        {
            "name": "Idiopathic Parkinson's disease",
            "confidence": 0.6,
            "icd_code": "G20",
            "reasoning": "Classic parkinsonian symptoms and MRI findings can occur in PD; no family history but age-appropriate onset; need to rule out to avoid missing primary neurodegenerative cause."
        },
        {
            "name": "Wilson's disease",
            "confidence": 0.4,
            "icd_code": "E83.01",
            "reasoning": "Basal ganglia abnormalities on MRI and neurological symptoms; copper metabolism disorder can mimic parkinsonism, though typically younger onset; geographic exposure irrelevant but testing warranted."
        },
        {
            "name": "Arsenic poisoning (chronic)",
            "confidence": 0.3,
            "icd_code": "T57.0X1A",
            "reasoning": "Endemic in Bangladesh groundwater; can cause neurological symptoms including movement disorders, though more commonly peripheral neuropathy; patient’s water project involvement raises suspicion despite temporal delay."
        }
    ],
    "key_findings": [
        "Progressive tremor, bradykinesia, and rigidity over 3 months",
        "Subtle basal ganglia signal abnormalities on MRI",
        "History of 2-year exposure in rural Bangladesh with symptoms starting 6 months post-return",
        "Patient self-research and request for environmental toxin testing"
    ],
    "diagnostic_tests": [
        {
            "test": "Serum and urine heavy metal screen (including manganese, arsenic, copper)",
            "purpose": "Rule in/out environmental toxin exposure as cause of parkinsonism",
            "priority": "immediate"
        },
        {
            "test": "DaTscan (SPECT imaging)",
            "purpose": "Differentiate idiopathic PD from secondary parkinsonism",
            "priority": "urgent"
        },
        {
            "test": "Serum ceruloplasmin and 24-hour urinary copper",
            "purpose": "Rule out Wilson's disease",
            "priority": "urgent"
        },
        {
            "test": "Repeat brain MRI with contrast",
            "purpose": "Further characterize basal ganglia abnormalities",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Admit for neurological evaluation and monitoring",
            "Initiate fall precautions due to movement disorder"
        ],
        "medications": [
            {
                "drug": "Levodopa/carbidopa",
                "dose": "25/100 mg",
                "route": "PO",
                "duration": "Titrate as needed, ongoing",
                "rationale": "Symptomatic relief of parkinsonian symptoms while awaiting definitive diagnosis"
            }
        ],
        "monitoring": [
            "Neurological status and vital signs",
            "Every 4 hours initially, then daily"
        ],
        "consultations": [
            {
                "specialty": "Neurology (Movement Disorders)",
                "urgency": "immediate",
                "reason": "Expert assessment of parkinsonism and differential"
            },
            {
                "specialty": "Toxicology",
                "urgency": "urgent",
                "reason": "Evaluate potential environmental exposures from Bangladesh assignment"
            },
            {
                "specialty": "Occupational Medicine",
                "urgency": "routine",
                "reason": "Assess occupational hazards from water purification projects"
            }
        ]
    },
    "red_flags": [
        "Rapid progression of symptoms suggesting aggressive neurodegenerative process",
        "Development of cognitive impairment or autonomic dysfunction",
        "Signs of acute toxicity such as gastrointestinal symptoms"
    ],
    "patient_education": [
        "Explain potential link between symptoms and past environmental exposures, but emphasize need for testing to confirm",
        "Advise avoiding self-diagnosis from online sources and discuss risks of untreated parkinsonism",
        "Discuss importance of medication adherence and fall prevention"
    ],
    "follow_up": {
        "timing": "1 week after discharge or test results",
        "conditions": "Worsening symptoms, falls, or new neurological deficits"
    },
    "confidence_in_assessment": 0.7,
    "uncertainties": [
        "Exact toxin involved and level of exposure during Bangladesh assignment",
        "Temporal relationship: 6-month delay post-return may not perfectly fit all toxin exposures",
        "Influence of patient-driven requests on diagnostic bias vs. objective assessment"
    ]
}
