{
    "primary_diagnosis": {
        "name": "Decompensated Congestive Heart Failure",
        "confidence": 0.86,
        "icd_code": "I50.9",
        "reasoning": "Progressive dyspnea, peripheral edema, elevated JVP, and basilar crackles in a patient with risk factors (HTN, diabetes) strongly suggest heart failure exacerbation."
    },
    "differential_diagnoses": [
        {
            "name": "Acute Coronary Syndrome (NSTEMI)",
            "confidence": 0.38,
            "icd_code": "I21.4",
            "reasoning": "Chest discomfort and dyspnea could represent myocardial ischemia, especially with hypertension and diabetes."
        },
        {
            "name": "Pulmonary Embolism",
            "confidence": 0.25,
            "icd_code": "I26.99",
            "reasoning": "Sudden dyspnea and chest discomfort; however, lack of pleuritic pain or risk factors makes it less likely."
        },
        {
            "name": "Chronic Obstructive Pulmonary Disease Exacerbation",
            "confidence": 0.20,
            "icd_code": "J44.1",
            "reasoning": "Baseline dyspnea could be pulmonary, but absence of smoking history and presence of volume overload signs favor heart failure."
        }
    ],
    "key_findings": [
        "Bilateral lower extremity edema",
        "Elevated jugular venous pressure",
        "Basilar crackles on lung exam"
    ],
    "diagnostic_tests": [
        {
            "test": "BNP or NT-proBNP",
            "purpose": "Confirm cardiac volume overload and support heart failure diagnosis",
            "priority": "immediate"
        },
        {
            "test": "Basic metabolic panel (BMP) with electrolytes and renal function",
            "purpose": "Assess kidney function and electrolyte status before diuresis",
            "priority": "immediate"
        },
        {
            "test": "Troponin I/T",
            "purpose": "Rule out myocardial injury/ACS",
            "priority": "immediate"
        },
        {
            "test": "12-lead ECG",
            "purpose": "Identify ischemic changes, arrhythmias, or LV hypertrophy",
            "priority": "immediate"
        },
        {
            "test": "Chest X‑ray",
            "purpose": "Evaluate pulmonary congestion, pleural effusion, or alternative pulmonary pathology",
            "priority": "urgent"
        },
        {
            "test": "Transthoracic echocardiogram",
            "purpose": "Assess ejection fraction, valvular disease, and structural causes of HF",
            "priority": "urgent"
        },
        {
            "test": "Lower extremity venous duplex ultrasound (if PE suspicion high)",
            "purpose": "Detect deep vein thrombosis as a PE source",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Arrange certified ASL interpreter for full history and consent",
            "Place patient on supplemental oxygen to maintain SpO2 > 94%",
            "Start IV loop diuretic (furosemide) and obtain baseline vitals and urine output"
        ],
        "medications": [
            {
                "drug": "Furosemide",
                "dose": "40 mg IV push; repeat 20 mg q6h as needed",
                "route": "IV",
                "duration": "Until euvolemia achieved, reassess daily",
                "rationale": "Rapid diuresis to reduce preload and relieve congestion"
            },
            {
                "drug": "Lisinopril",
                "dose": "10 mg PO daily",
                "route": "PO",
                "duration": "Long‑term heart failure therapy",
                "rationale": "Afterload reduction and mortality benefit in HFrEF"
            },
            {
                "drug": "Metoprolol succinate",
                "dose": "25 mg PO daily (start low, titrate)",
                "route": "PO",
                "duration": "Long‑term heart failure therapy",
                "rationale": "Beta‑blockade for remodeling and symptom control"
            }
        ],
        "monitoring": [
            "Daily weight, intake/output, and peripheral edema",
            "Serum electrolytes, BUN/creatinine q24h while on diuretics",
            "Blood pressure and heart rate q4h initially"
        ],
        "consultations": [
            {
                "specialty": "Cardiology",
                "urgency": "urgent",
                "reason": "Guidance on HF severity, medication titration, and possible advanced therapies"
            },
            {
                "specialty": "Pulmonology",
                "urgency": "routine",
                "reason": "If respiratory symptoms persist after volume optimization"
            }
        ]
    },
    "red_flags": [
        "Sudden worsening dyspnea or orthopnea",
        "Chest pain radiating to arm/jaw or associated with diaphoresis",
        "Syncope or presyncope",
        "Rapid weight gain (>2 kg in 24 h) or worsening edema"
    ],
    "patient_education": [
        "Explain heart failure pathophysiology using visual aids and written material in clear language",
        "Emphasize daily weight monitoring, low‑sodium diet (<2 g Na/day), and adherence to prescribed medications"
    ],
    "follow_up": {
        "timing": "Return to primary care or HF clinic in 2–3 days after discharge",
        "conditions": "Seek immediate care for worsening shortness of breath, new chest pain, fainting, or rapid weight gain"
    },
    "confidence_in_assessment": 0.88,
    "uncertainties": [
        "Exact contribution of acute coronary ischemia to chest discomfort without definitive ECG/biomarker results yet",
        "Baseline left ventricular ejection fraction pending echocardiogram"
    ]
}
