    "primary_diagnosis": {
        "name": "Deep vein thrombosis (DVT) of the left calf",
        "confidence": 0.7,
        "icd_code": "I82.40",
        "reasoning": "The sudden onset of severe left calf pain and swelling, along with asymmetric calf circumference and tenderness on examination, are highly suggestive of DVT. While tachycardia could be related to the patient's anxiety, it may also be a sign of a hypercoagulable state, which is a known risk factor for transgender women on estradiol therapy."
    },
    "differential_diagnoses": [
        {
            "name": "Cellulitis",
            "confidence": 0.2,
            "icd_code": "L03.2",
            "reasoning": "The presentation of calf pain and swelling could indicate an infection, especially if the area is warm and erythematous. However, the absence of fever and the patient's history make this less likely."
        },
        {
            "name": "Muscle strain or hematoma",
            "confidence": 0.1,
            "icd_code": "S76.009A",
            "reasoning": "A muscle strain or hematoma could cause similar symptoms, but the asymmetric calf circumference and tenderness suggest a vascular issue, making this diagnosis less likely."
        },
        {
            "name": "Anxiety-related symptoms",
            "confidence": 0.1,
            "icd_code": "F41.1",
            "reasoning": "The patient's history of anxiety and tachycardia could be indicative of a panic attack or heightened anxiety. However, the physical examination findings of asymmetric calf circumference and tenderness are more consistent with a vascular issue."
        }
    ],
    "key_findings": [
        "Sudden-onset severe left calf pain and swelling",
        "Asymmetric calf circumference and tenderness",
        "Tachycardia (HR 110)",
        "Oral estradiol therapy for gender affirmation"
    ],
    "diagnostic_tests": [
        {
            "test": "Doppler ultrasound of the left lower extremity",
            "purpose": "Rule in/out DVT",
            "priority": "urgent"
        },
        {
            "test": "Complete blood count (CBC) and comprehensive metabolic panel (CMP)",
            "purpose": "Evaluate for infection or other underlying conditions",
            "priority": "routine"
        },
        {
            "test": "Coagulation studies",
            "purpose": "Evaluate for hypercoagulability",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Elevate the affected leg",
            "Apply compression stockings to reduce swelling"
        ],
        "medications": [
            {
                "drug": "Low-molecular-weight heparin (e.g., enoxaparin)",
                "dose": "40 mg subcutaneously once daily",
                "route": "Subcutaneous",
                "duration": "5-7 days or until oral anticoagulants can be initiated",
                "rationale": "To prevent further clot formation and reduce the risk of pulmonary embolism"
            },
            {
                "drug": "Antibiotics (e.g., cephalexin or dicloxacillin)",
                "dose": "Dicloxacillin: 500 mg orally every 6 hours; Cephalexin: 500 mg orally every 12 hours",
                "route": "Oral",
                "duration": "7-10 days",
                "rationale": "To address the possibility of cellulitis or prevent potential infection in the affected area"
            }
        ],
        "monitoring": [
            "Repeat Doppler ultrasound in 5-7 days to assess clot resolution",
            "Monitor anticoagulant therapy with regular INR/PTT tests"
        ],
        "consultations": [
            {
                "specialty": "Hematology",
                "urgency": "routine",
                "reason": "Further evaluation of hypercoagulability and long-term anticoagulation management"
            }
        ]
    },
    "red_flags": [
        "Shortness of breath or chest pain: Possible signs of pulmonary embolism",
        "Fever and increased pain: Possible signs of infection or worsening cellulitis"
    ],
    "patient_education": [
        "Elevate the leg whenever possible to reduce swelling",
        "Take prescribed medications as directed",
        "Follow up with the clinic or seek immediate care if symptoms worsen or new concerning symptoms develop"
    ],
    "follow_up": {
        "timing": "In 1 week or earlier if symptoms worsen",
        "conditions": "Immediate return if patient experiences shortness of breath, chest pain, fever, or increased pain and swelling"
    },
    "confidence_in_assessment": 0.75,
    "uncertainties": [
        "The exact cause of tachycardia cannot be determined without further evaluation",
        "The contribution of anxiety to the patient's presentation is unclear"
    ]
