{
    "primary_diagnosis": {
        "name": "Deep vein thrombosis (DVT)",
        "confidence": 0.85,
        "icd_code": "I82.401",
        "reasoning": "The sudden onset of severe calf pain and swelling, along with asymmetric calf circumference and tenderness, are classic signs of DVT, particularly in a patient on estrogen therapy which increases the risk."
    },
    "differential_diagnoses": [
        {
            "name": "Muscle strain",
            "confidence": 0.15,
            "icd_code": "S76.0XXA",
            "reasoning": "Could account for calf pain and swelling but less likely due to sudden onset and physical exam findings."
        },
        {
            "name": "Compartment syndrome",
            "confidence": 0.05,
            "icd_code": "S76.3XXA",
            "reasoning": "Considered due to severe pain and swelling but less likely given the absence of neurological deficits."
        },
        {
            "name": "Superficial thrombophlebitis",
            "confidence": 0.05,
            "icd_code": "I80.0",
            "reasoning": "Possible but less likely as the predominant symptom of severe pain and swelling suggests a deeper issue."
        }
    ],
    "key_findings": [
        "Asymmetric calf circumference",
        "Tenderness in the left calf"
    ],
    "diagnostic_tests": [
        {
            "test": "Doppler ultrasound of the left leg",
            "purpose": "To confirm or rule out DVT",
            "priority": "urgent"
        },
        {
            "test": "D-dimer test",
            "purpose": "To assist in ruling out thromboembolism",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Obtain Doppler ultrasound",
            "Start compression therapy for swelling"
        ],
        "medications": [
            {
                "drug": "Enoxaparin",
                "dose": "1 mg/kg SC every 12 hours",
                "route": "SC",
                "duration": "Until therapeutic anticoagulation achieved",
                "rationale": "To prevent further clot formation in suspected DVT."
            }
        ],
        "monitoring": [
            "Monitor vital signs and symptoms of pulmonary embolism",
            "Every 4 hours"
        ],
        "consultations": [
            {
                "specialty": "Vascular surgery",
                "urgency": "urgent",
                "reason": "To evaluate for potential intervention if DVT is confirmed."
            }
        ]
    },
    "red_flags": [
        "Respiratory distress",
        "Chest pain or hemoptysis"
    ],
    "patient_education": [
        "Importance of early identification and treatment of DVT",
        "Potential risks associated with hormone therapy"
    ],
    "follow_up": {
        "timing": "Follow up in 1 week or sooner if symptoms worsen",
        "conditions": "Any signs of pulmonary embolism or worsening leg symptoms"
    },
    "confidence_in_assessment": 0.8,
    "uncertainties": [
        "Possible concurrent conditions that may complicate diagnosis",
        "Need for further imaging if initial tests are inconclusive"
    ]
}
