{
    "primary_diagnosis": {
        "name": "Deep vein thrombosis of the left lower extremity (calf)",
        "confidence": 0.92,
        "icd_code": "I82.409",
        "reasoning": "Acute unilateral calf swelling, pain, increased circumference, and risk factor of chronic oral estrogen therapy strongly suggest a distal DVT. Tachycardia may reflect pain or early pulmonary embolism."
    },
    "differential_diagnoses": [
        {
            "name": "Cellulitis of the lower leg",
            "confidence": 0.30,
            "icd_code": "L03.90",
            "reasoning": "Unilateral swelling and tenderness can be infectious; however, lack of erythema, warmth, fever, and presence of risk factor for thrombosis lower its likelihood."
        },
        {
            "name": "Muscle strain / tear of gastrocnemius or soleus",
            "confidence": 0.25,
            "icd_code": "M62.60",
            "reasoning": "Acute calf pain after activity could be muscular, but swelling and disproportionate circumference are more typical of venous thrombosis."
        },
        {
            "name": "Superficial thrombophlebitis",
            "confidence": 0.20,
            "icd_code": "I80.1",
            "reasoning": "Inflammation of superficial veins can cause localized pain and swelling, but usually presents with a palpable cord and less marked calf girth increase."
        }
    ],
    "key_findings": [
        "Sudden onset severe left calf pain",
        "Unilateral increase in calf circumference with tenderness",
        "Chronic oral estradiol therapy (estrogen exposure)",
        "Tachycardia (HR 110) without fever"
    ],
    "diagnostic_tests": [
        {
            "test": "Compression duplex ultrasonography of the left lower extremity",
            "purpose": "Rule in/out DVT and determine extent (distal vs proximal)",
            "priority": "immediate"
        },
        {
            "test": "D-dimer assay",
            "purpose": "Supportive test; a negative result could lower suspicion if pre‑test probability is low",
            "priority": "urgent"
        },
        {
            "test": "CBC with differential and basic metabolic panel",
            "purpose": "Baseline labs before anticoagulation; assess for infection or anemia",
            "priority": "routine"
        },
        {
            "test": "Coagulation profile (PT/INR, aPTT)",
            "purpose": "Baseline for anticoagulation safety",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Place the patient on cardiac and pulse oximetry monitoring",
            "Elevate the left leg and encourage ambulation as tolerated",
            "Obtain immediate lower‑extremity duplex ultrasound"
        ],
        "medications": [
            {
                "drug": "Enoxaparin (low molecular weight heparin)",
                "dose": "1 mg/kg subcutaneously every 12 hours",
                "route": "SC",
                "duration": "Until outpatient anticoagulation is established (minimum 5 days)",
                "rationale": "Rapid anticoagulation for confirmed or highly suspected DVT"
            },
            {
                "drug": "Rivaroxaban",
                "dose": "15 mg orally twice daily for 21 days, then 20 mg daily",
                "route": "PO",
                "duration": "Minimum 3 months, then reassess duration",
                "rationale": "Direct oral anticoagulant for treatment and transition from LMWH"
            }
        ],
        "monitoring": [
            "Vital signs (HR, BP, O2 saturation) every 1–2 hours while in urgent care",
            "Signs of bleeding (gastrointestinal, hematuria, ecchymoses) daily",
            "Serial assessment of leg circumference and pain"
        ],
        "consultations": [
            {
                "specialty": "Vascular Medicine / Hematology",
                "urgency": "urgent",
                "reason": "Confirm diagnosis, guide anticoagulation duration, evaluate need for thrombophilia work‑up"
            },
            {
                "specialty": "Endocrinology",
                "urgency": "routine",
                "reason": "Review estrogen therapy risk and discuss alternatives or dose adjustment"
            }
        ]
    },
    "red_flags": [
        "Sudden onset chest pain or dyspnea (possible pulmonary embolism)",
        "Rapid progression of leg swelling with cyanosis or loss of pulses (compartment syndrome or arterial occlusion)",
        "Severe abdominal or back pain suggesting extension of clot"
    ],
    "patient_education": [
        "Explain that estrogen increases clot risk and that anticoagulation is essential to prevent complications.",
        "Instruct on signs of bleeding (e.g., unusual bruising, blood in urine or stool, severe headache) and when to seek immediate care."
    ],
    "follow_up": {
        "timing": "Return to urgent care or vascular clinic within 48–72 hours for ultrasound results and anticoagulation review.",
        "conditions": "Seek emergency care if new shortness of breath, chest pain, coughing up blood, worsening leg pain, or signs of major bleeding."
    },
    "confidence_in_assessment": 0.93,
    "uncertainties": [
        "Exact proximal versus distal extent of the thrombus pending duplex imaging.",
        "Presence of an underlying inherited thrombophilia contributing to recurrent risk."
    ]
}
