{
    "primary_diagnosis": {
        "name": "Acute Deep Vein Thrombosis, Left Lower Extremity",
        "confidence": 0.85,
        "icd_code": "I82.402",
        "reasoning": "Sudden onset unilateral calf pain, swelling, tenderness, and asymmetric calf circumference in transgender woman on estrogen therapy for 4 years. Estrogen significantly increases VTE risk (up to 5% in transgender women). Physical findings classic for DVT."
    },
    "differential_diagnoses": [
        {
            "name": "Cellulitis of Left Lower Leg",
            "confidence": 0.20,
            "icd_code": "L03.116",
            "reasoning": "Can present with calf pain, swelling, and tenderness, but typically includes erythema, warmth, and fever which are not prominent here"
        },
        {
            "name": "Muscle Strain/Injury, Left Calf",
            "confidence": 0.15,
            "icd_code": "S86.111A",
            "reasoning": "Could cause calf pain and some swelling, but sudden onset without trauma history and asymmetric swelling make this less likely"
        },
        {
            "name": "Panic Disorder with Physical Symptoms",
            "confidence": 0.05,
            "icd_code": "F41.0",
            "reasoning": "Initially considered by staff due to anxiety history and tachycardia, but physical findings of unilateral calf swelling and tenderness cannot be explained by panic alone"
        }
    ],
    "key_findings": [
        "Unilateral left calf pain and swelling with sudden onset",
        "Asymmetric calf circumference with tenderness",
        "4-year history of oral estradiol therapy (major VTE risk factor)",
        "Tachycardia (HR 110) possibly secondary to pain or PE"
    ],
    "diagnostic_tests": [
        {
            "test": "Duplex ultrasound of left lower extremity",
            "purpose": "Confirm or rule out deep vein thrombosis",
            "priority": "immediate"
        },
        {
            "test": "D-dimer",
            "purpose": "Support DVT diagnosis if elevated (though has limited specificity)",
            "priority": "urgent"
        },
        {
            "test": "Wells Score calculation",
            "purpose": "Stratify DVT risk and guide diagnostic approach",
            "priority": "immediate"
        },
        {
            "test": "CT Pulmonary Angiogram (CTPA)",
            "purpose": "Rule out pulmonary embolism if patient has chest symptoms or high clinical suspicion",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Obtain duplex ultrasound of left lower extremity stat",
            "Calculate Wells score for DVT risk stratification",
            "Assess for signs/symptoms of pulmonary embolism",
            "Establish IV access if not already present"
        ],
        "medications": [
            {
                "drug": "Enoxaparin",
                "dose": "1 mg/kg subcutaneously every 12 hours",
                "route": "SubQ",
                "duration": "Until therapeutic on warfarin or DOAC",
                "rationale": "Immediate anticoagulation if DVT confirmed while awaiting oral anticoagulant therapeutic levels"
            },
            {
                "drug": "Acetaminophen",
                "dose": "650-1000 mg every 6 hours as needed",
                "route": "PO",
                "duration": "As needed for pain",
                "rationale": "Pain management while avoiding NSAIDs which may increase bleeding risk"
            }
        ],
        "monitoring": [
            "Vital signs every 4 hours",
            "Daily assessment of calf circumference and pain level",
            "Watch for signs of pulmonary embolism",
            "Monitor for bleeding if anticoagulation started"
        ],
        "consultations": [
            {
                "specialty": "Hematology",
                "urgency": "urgent",
                "reason": "Management of VTE in transgender patient on hormone therapy, risk-benefit of continuing estrogen"
            },
            {
                "specialty": "Endocrinology",
                "urgency": "routine",
                "reason": "Optimize hormone therapy management and discuss VTE risk reduction strategies"
            }
        ]
    },
    "red_flags": [
        "Chest pain, shortness of breath, or hemoptysis suggesting pulmonary embolism",
        "Worsening leg pain, swelling, or skin color changes",
        "Signs of compartment syndrome",
        "Any bleeding complications if anticoagulation initiated"
    ],
    "patient_education": [
        "Explain increased VTE risk associated with estrogen therapy and importance of symptom recognition",
        "Discuss signs and symptoms of pulmonary embolism requiring immediate medical attention",
        "Importance of medication compliance if anticoagulation is started",
        "Activity modifications and leg elevation to reduce swelling"
    ],
    "follow_up": {
        "timing": "Within 24-48 hours after discharge or sooner if symptoms worsen",
        "conditions": "Return immediately for chest pain, shortness of breath, severe bleeding, or worsening leg symptoms"
    },
    "confidence_in_assessment": 0.80,
    "uncertainties": [
        "Exact duration and specific formulation of estrogen therapy may affect VTE risk assessment",
        "Need to rule out pulmonary embolism given tachycardia",
        "Optimal management of hormone therapy after VTE event requires specialist input"
    ]
}
