    "key_findings": [
        "Sudden onset severe left calf pain and swelling",
        "Asymmetric calf circumference and tenderness"
    ],
    "diagnostic_tests": [
        {
            "test": "Duplex ultrasound of lower extremities",
            "purpose": "To confirm or rule out DVT",
            "priority": "immediate"
