        "immediate_actions": [
            "Initiate anticoagulation therapy pending ultrasound results",
            "Elevate the affected leg"
        ],
        "medications": [
            {
                "drug": "Enoxaparin",
                "dose": "1mg/kg subcutaneously every 12 hours",
                "route": "SC",
                "duration": "Until transitioned to oral anticoagulation",
                "rationale": "To prevent clot extension and embolization"
            }
        ],
        "monitoring": [
            "Heart rate and blood pressure",
            "Every 4 hours until stable"
        ],
        "consultations": [
            {
                "specialty": "Hematology",
                "urgency": "urgent",
                "reason": "To manage anticoagulation and assess for underlying thrombophilia"
            }
        ]
    },
