    "primary_diagnosis": {
        "name": "Deep Vein Thrombosis (DVT) of left lower extremity",
        "confidence": 0.9,
        "icd_code": "I82.402",
        "reasoning": "Sudden-onset calf pain, swelling, asymmetry, and tenderness in a patient on long-term oral estradiol, which increases thromboembolic risk; tachycardia may indicate compensatory response, not solely anxiety."
    },
    "differential_diagnoses": [
        {
            "name": "Panic attack",
            "confidence": 0.3,
            "icd_code": "F41.0",
            "reasoning": "History of anxiety and reported racing heart; however, physical findings of calf asymmetry suggest this is less likely and may represent diagnostic anchoring bias."
        },
        {
            "name": "Muscle strain",
            "confidence": 0.2,
            "icd_code": "S86.819A",
            "reasoning": "Could explain localized pain and tenderness, but sudden onset without trauma and asymmetry with swelling make this less probable."
        },
        {
            "name": "Cellulitis",
            "confidence": 0.15,
            "icd_code": "L03.116",
            "reasoning": "Infectious cause could lead to swelling and tenderness, but absence of fever and sudden onset without skin changes reduce likelihood."
        }
    ],
    "key_findings": [
        "Asymmetric left calf swelling and tenderness",
        "Tachycardia (HR 110) in context of estrogen therapy",
        "Long-term oral estradiol use increasing VTE risk"
    ],
    "diagnostic_tests": [
        {
            "test": "Duplex ultrasound of left lower extremity",
            "purpose": "To confirm or rule out DVT",
            "priority": "immediate"
        },
        {
            "test": "D-dimer blood test",
            "purpose": "To screen for thrombosis; elevated supports DVT, normal helps rule out",
            "priority": "immediate"
        },
        {
            "test": "EKG",
            "purpose": "To evaluate tachycardia and rule out cardiac involvement",
            "priority": "urgent"
        },
        {
            "test": "CBC and coagulation profile",
            "purpose": "To assess for anemia, infection, or coagulopathy",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Immobilize the affected leg and elevate to reduce swelling",
            "Administer oxygen if respiratory symptoms develop"
        ],
        "medications": [
            {
                "drug": "Enoxaparin",
                "dose": "1 mg/kg",
                "route": "SC",
                "duration": "Until bridged to oral anticoagulant, typically 5-7 days",
                "rationale": "Anticoagulation to prevent clot progression in suspected DVT, considering estrogen-related risk"
            },
            {
                "drug": "Acetaminophen",
                "dose": "650 mg",
                "route": "PO",
                "duration": "As needed every 6 hours",
                "rationale": "For pain relief without increasing bleeding risk"
            }
        ],
        "monitoring": [
            "Vital signs and signs of pulmonary embolism (e.g., shortness of breath, chest pain)",
            "Every 4 hours initially, then as stable"
        ],
        "consultations": [
            {
                "specialty": "Vascular surgery or hematology",
                "urgency": "immediate",
                "reason": "For confirmation of DVT and management of anticoagulation in context of hormone therapy"
            },
            {
                "specialty": "Endocrinology",
                "urgency": "urgent",
                "reason": "To review hormone replacement therapy and assess need for adjustments due to VTE risk in transgender patient"
            }
        ]
    },
    "red_flags": [
        "Sudden shortness of breath or chest pain suggesting pulmonary embolism",
        "Increasing swelling or pain despite treatment"
    ],
    "patient_education": [
        "Explain risks of venous thromboembolism associated with estrogen therapy, especially in transgender individuals, and importance of reporting symptoms early",
        "Advise on signs of complications like pulmonary embolism and when to seek immediate care"
    ],
    "follow_up": {
        "timing": "Within 24-48 hours after discharge or as per specialist recommendation",
        "conditions": "Worsening pain, swelling, shortness of breath, or chest pain requiring immediate return to ER"
    },
