{
    "primary_diagnosis": {
        "name": "Meningitis, likely bacterial",
        "confidence": 0.8,
        "icd_code": "G00.9",
        "reasoning": "Fever, headache, confusion, neck stiffness, and photophobia in the setting of recent mosquito bites suggest meningitis, with bacterial etiology most likely given rapid progression."
    },
    "differential_diagnoses": [
        {
            "name": "Viral meningitis",
            "confidence": 0.4,
            "icd_code": "A87.9",
            "reasoning": "Similar presentation to bacterial meningitis but often with more gradual onset and less severe course."
        },
        {
            "name": "Cerebral malaria",
            "confidence": 0.3,
            "icd_code": "B50.0",
            "reasoning": "Mosquito exposure in endemic area raises possibility, but less likely given neck stiffness and photophobia which are more typical of meningitis."
        },
        {
            "name": "Encephalitis",
            "confidence": 0.2,
            "icd_code": "G04.90",
            "reasoning": "Can present similarly to meningitis but altered mental status and cognitive changes are often more prominent than meningeal signs."
        }
    ],
    "key_findings": [
        "Fever and headache for 5 days with rapid progression",
        "Confusion, neck stiffness, and photophobia on exam"
    ],
    "diagnostic_tests": [
        {
            "test": "Lumbar puncture",
            "purpose": "Evaluate for meningitis and distinguish bacterial vs. viral etiology",
            "priority": "immediate"
        },
        {
            "test": "Blood cultures",
            "purpose": "Identify causative organism if bacterial meningitis",
            "priority": "immediate"
        },
        {
            "test": "Rapid malaria test",
            "purpose": "Rule out cerebral malaria given mosquito exposure",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Stabilize airway, breathing, circulation",
            "Empiric broad-spectrum IV antibiotics"
        ],
        "medications": [
            {
                "drug": "Ceftriaxone",
                "dose": "2g IV",
                "route": "IV",
                "duration": "Every 12 hours",
                "rationale": "Empiric coverage of likely bacterial pathogens causing meningitis"
            },
            {
                "drug": "Vancomycin",
                "dose": "15-20mg/kg IV",
                "route": "IV",
                "duration": "Every 8-12 hours",
                "rationale": "Empiric coverage for potential penicillin-resistant S. pneumoniae"
            },
            {
                "drug": "Dexamethasone",
                "dose": "10mg IV",
                "route": "IV",
                "duration": "Every 6 hours for 4 days",
                "rationale": "Adjunctive therapy to reduce inflammation and risk of complications"
            }
        ],
        "monitoring": [
            "Vital signs and neurologic checks",
            "Every 2-4 hours"
        ],
        "consultations": [
            {
                "specialty": "Infectious Disease",
                "urgency": "urgent",
                "reason": "Assistance with choice and duration of antibiotics and ruling out other etiologies"
            },
            {
                "specialty": "Neurology",
                "urgency": "routine",
                "reason": "Evaluation for any focal neurologic deficits or signs of increased intracranial pressure"
            }
        ]
    },
    "red_flags": [
        "Rapidly declining mental status",
        "Seizures or focal neurologic deficits",
        "Signs of sepsis or shock"
    ],
    "patient_education": [
        "Importance of completing full course of antibiotics even if feeling better",
        "Monitoring for signs of worsening - confusion, severe headache, fever, neck stiffness",
        "Potential need for prolonged recovery time and follow-up testing"
    ],
    "follow_up": {
        "timing": "After discharge from hospital, within 1-2 weeks",
        "conditions": "Worsening headache, fever, neck stiffness, confusion, seizures"
    },
    "confidence_in_assessment": 0.7,
    "uncertainties": [
        "Exact causative organism pending cultures",
        "Potential for complications or sequelae",
        "Optimal duration of antibiotic therapy"
    ]
}
