        "Fever",
        "Headache",
        "Neck stiffness",
        "Photophobia",
        "Disorientation and confusion"
    ],
    "diagnostic_tests": [
        {
            "test": "Lumbar Puncture (Spinal Tap)",
            "purpose": "Rule in/out meningitis, encephalitis, or other central nervous system infections",
            "priority": "urgent"
        },
        {
            "test": "Complete Blood Count (CBC) with Differential",
            "purpose": "Evaluate for infection and inflammation",
            "priority": "routine"
        },
        {
            "test": "Serology for Arboviruses (including West Nile Virus)",
            "purpose": "Detect evidence of recent viral infection transmitted by mosquitoes",
            "priority": "routine"
        },
        {
            "test": "Electroencephalogram (EEG)",
            "purpose": "Evaluate brain activity for seizures or other abnormalities",
            "priority": "routine"
        },
        {
            "test": "Magnetic Resonance Imaging (MRI) of Brain",
            "purpose": "Visualize brain for signs of inflammation, infection, or structural abnormalities",
            "priority": "routine"
        }
    ],
