        "medications": [
            {
                "drug": "Ceftriaxone",
                "dose": "2 grams intravenously every 12 hours",
                "route": "IV",
                "duration": "7-14 days or as guided by lumbar puncture results",
                "rationale": "Broad-spectrum antibiotic effective against common causes of bacterial meningitis"
