{
    "primary_diagnosis": {
        "name": "Acute bacterial meningitis",
        "confidence": 0.72,
        "icd_code": "G00.9",
        "reasoning": "Fever, neck stiffness, photophobia, altered mental status in a previously healthy adult are classic for meningitis. In a resource‑limited setting, bacterial meningitis must be prioritized because of high mortality without prompt therapy."
    },
    "differential_diagnoses": [
        {
            "name": "Viral encephalitis (e.g., West Nile virus)",
            "confidence": 0.18,
            "icd_code": "A92.3",
            "reasoning": "Recent mosquito exposure and the same triad of fever, headache, and confusion can be caused by arboviral encephalitis; however, neck stiffness is less prominent than in meningitis."
        },
        {
            "name": "Cerebral malaria",
            "confidence": 0.07,
            "icd_code": "B50.9",
            "reasoning": "Mosquito bites and fever raise suspicion for malaria; neurological involvement can mimic meningitis, especially in endemic rural areas."
        },
        {
            "name": "Tuberculous meningitis",
            "confidence": 0.03,
            "icd_code": "A17.0",
            "reasoning": "Subacute presentation can be similar, but the rapid 5‑day progression and lack of chronic risk factors make it less likely."
        }
    ],
    "key_findings": [
        "Fever for 5 days",
        "Neck stiffness with photophobia and confusion"
    ],
    "diagnostic_tests": [
        {
            "test": "Lumbar puncture with CSF analysis (cell count, glucose, protein, Gram stain, culture)",
            "purpose": "Rule in bacterial meningitis vs. viral or other causes",
            "priority": "immediate"
        },
        {
            "test": "Rapid malaria antigen test (RDT)",
            "purpose": "Exclude cerebral malaria as an alternative etiology",
            "priority": "urgent"
        },
        {
            "test": "Blood cultures (2 sets)",
            "purpose": "Identify bloodstream pathogen for targeted antibiotics",
            "priority": "urgent"
        },
        {
            "test": "Basic metabolic panel and complete blood count",
            "purpose": "Assess electrolytes, renal function, and inflammatory markers",
            "priority": "routine"
        },
        {
            "test": "If available, point‑of‑care ultrasound for signs of increased intracranial pressure",
            "purpose": "Guide safety of immediate lumbar puncture",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Begin empiric intravenous antibiotics: ceftriaxone 2 g IV q24h plus vancomycin 15 mg/kg IV q12h",
            "Administer dexamethasone 10 mg IV before or with first antibiotic dose",
            "Obtain blood cultures and rapid malaria test before antibiotics if possible",
            "Perform bedside lumbar puncture if no contraindication; send CSF for analysis"
        ],
        "medications": [
            {
                "drug": "Ceftriaxone",
                "dose": "2 g IV once daily",
                "route": "IV",
                "duration": "10–14 days (adjust based on culture results)",
                "rationale": "Broad‑spectrum coverage for common bacterial meningitis pathogens"
            },
            {
                "drug": "Vancomycin",
                "dose": "15 mg/kg IV every 12 hours (adjust for renal function)",
                "route": "IV",
                "duration": "10–14 days",
                "rationale": "Provides coverage for resistant Streptococcus pneumoniae"
            },
            {
                "drug": "Dexamethasone",
                "dose": "10 mg IV once (may repeat q6h for 4 doses)",
                "route": "IV",
                "duration": "4 days total",
                "rationale": "Reduces inflammatory complications in bacterial meningitis"
            },
            {
                "drug": "Artemisinin‑based combination therapy (e.g., artesunate + sulfadoxine‑pyrimethamine)",
                "dose": "If rapid malaria test positive, follow national protocol",
                "route": "IV/PO",
                "duration": "According to malaria guidelines",
                "rationale": "Treat cerebral malaria if present"
            }
        ],
        "monitoring": [
            "Neurological status (Glasgow Coma Scale) – hourly",
            "Vital signs (temperature, blood pressure, heart rate, respiratory rate) – q2h",
            "Fluid balance and electrolytes – q8h"
        ],
        "consultations": [
            {
                "specialty": "Infectious disease",
                "urgency": "urgent",
                "reason": "Guidance on antimicrobial regimen and duration, especially if atypical pathogen suspected"
            },
            {
                "specialty": "Neurology",
                "urgency": "urgent",
                "reason": "If neurological deterioration or need for advanced neuroimaging"
            },
            {
                "specialty": "Transport/EMS",
                "urgency": "immediate",
                "reason": "Arrange rapid transfer to tertiary center for possible ICU care"
            }
        ]
    },
    "red_flags": [
        "Rapid worsening of mental status or development of seizures",
        "Signs of increased intracranial pressure (papilledema, bradycardia, hypertension)"
    ],
    "patient_education": [
        "Explain the seriousness of meningitis and the need for immediate treatment",
        "Discuss signs that require immediate return: new seizures, worsening confusion, severe headache, vomiting"
    ],
    "follow_up": {
        "timing": "Re‑evaluate in the clinic within 24 hours or sooner if condition changes; after transfer, follow infectious disease follow‑up per hospital protocol",
        "conditions": "Any new neurologic deficit, persistent fever after 48 h of therapy, or adverse drug reactions"
    },
    "confidence_in_assessment": 0.78,
    "uncertainties": [
        "Exact causative pathogen pending CSF and blood culture results",
        "Whether arboviral infection is co‑present given mosquito exposure"
    ]
}
