content:
{
    "primary_diagnosis": {
        "name": "Acute bacterial meningitis",
        "confidence": 0.75,
        "icd_code": "G00.9",
        "reasoning": "Classic triad of fever, neck stiffness, and altered mental status with photophobia in older adult requires immediate empiric treatment"
    },
    "differential_diagnoses": [
        {
            "name": "West Nile virus encephalitis",
            "confidence": 0.70,
            "icd_code": "A92.31",
            "reasoning": "Recent mosquito bites, neuroinvasive disease symptoms, older adult at higher risk for severe illness"
        },
        {
            "name": "Eastern equine encephalitis",
            "confidence": 0.40,
            "icd_code": "A83.2",
            "reasoning": "Mosquito-borne illness with encephalitis symptoms, though less common than West Nile"
        },
        {
            "name": "Meningococcal meningitis",
            "confidence": 0.45,
            "icd_code": "A39.0",
            "reasoning": "Acute onset meningitis with classic symptoms, though no rash reported"
        }
    ],
    "key_findings": [
        "Classic meningitis triad: fever, neck stiffness, altered mental status",
        "Photophobia indicating meningeal irritation",
        "Recent mosquito bite exposure in rural setting",
        "Progressive 5-day course with neurological deterioration"
    ],
    "diagnostic_tests": [
        {
            "test": "Lumbar puncture with CSF analysis",
            "purpose": "Differentiate bacterial from viral meningitis, identify organism",
            "priority": "immediate"
        },
        {
            "test": "Blood cultures",
            "purpose": "Identify causative organism in bacterial meningitis",
            "priority": "immediate"
        },
        {
            "test": "Complete blood count with differential",
            "purpose": "Assess for bacterial infection and complications",
            "priority": "immediate"
        },
        {
            "test": "Basic metabolic panel",
            "purpose": "Assess electrolyte balance and renal function",
            "priority": "urgent"
        },
        {
            "test": "C-reactive protein and procalcitonin",
            "purpose": "Distinguish bacterial from viral infection",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Initiate empiric antibiotic therapy immediately",
            "Start dexamethasone before or with first antibiotic dose",
            "Establish IV access and ensure adequate hydration",
            "Monitor neurological status and vital signs closely"
        ],
        "medications": [
            {
                "drug": "Ceftriaxone",
                "dose": "2g",
                "route": "IV",
                "duration": "Every 12 hours pending culture results",
                "rationale": "Empiric coverage for common bacterial pathogens"
            },
            {
                "drug": "Ampicillin",
                "dose": "2g",
                "route": "IV", 
                "duration": "Every 4 hours pending culture results",
                "rationale": "Coverage for Listeria monocytogenes in older adult"
            },
            {
                "drug": "Dexamethasone",
                "dose": "10mg",
                "route": "IV",
                "duration": "Every 6 hours for 4 days",
                "rationale": "Reduce inflammation and improve outcomes in bacterial meningitis"
            },
            {
                "drug": "Acyclovir",
                "dose": "10mg/kg",
                "route": "IV",
                "duration": "Every 8 hours for 10 days",
                "rationale": "Cover viral encephalitis until ruled out"
            }
        ],
        "monitoring": [
            "Neurological assessments every 2 hours",
            "Vital signs every 15 minutes initially, then hourly",
            "Urine output and fluid balance",
            "Signs of increased intracranial pressure"
        ],
        "consultations": [
            {
                "specialty": "Infectious Disease",
                "urgency": "urgent",
                "reason": "Guidance on antimicrobial therapy and vector-borne disease evaluation"
            },
            {
                "specialty": "Critical Care",
                "urgency": "immediate",
                "reason": "Potential need for ICU-level monitoring and airway management"
            },
            {
                "specialty": "Neurology",
                "urgency": "urgent",
                "reason": "Evaluation of encephalitis and neurological complications"
            }
        ]
    },
    "red_flags": [
        "Rapid deterioration in consciousness level",
        "Signs of increased intracranial pressure (bradycardia, hypertension, irregular breathing)",
        "Seizure activity",
        "Focal neurological deficits",
        "Petechial or purpuric rash development"
    ],
    "patient_education": [
        "Explain serious nature of condition requiring immediate treatment",
        "Discuss need for transfer to higher level of care",
        "Inform family about monitoring requirements and potential complications",
        "Emphasize importance of vector control measures in rural settings"
    ],
    "follow_up": {
        "timing": "Immediate transfer to tertiary care center for ongoing management",
        "conditions": "Any worsening neurological symptoms, fever persistence after 48-72 hours of treatment, or development of complications"
    },
    "confidence_in_assessment": 0.80,
    "uncertainties": [
        "Unable to distinguish bacterial from viral meningitis without CSF analysis",
        "Limited diagnostic capabilities in rural setting may delay definitive diagnosis",
        "Specific vector-borne pathogen identification requires specialized testing",
        "Timing of symptom onset relative to mosquito exposure unclear"
    ]
}
