{
    "primary_diagnosis": {
        "name": "Arboviral Encephalitis",
        "confidence": 0.75,
        "icd_code": "A85.2",
        "reasoning": "Progressive fever, headache, confusion, neck stiffness, photophobia, and disorientation in a rural farmer with recent mosquito bites suggest mosquito-borne viral infection like Japanese Encephalitis, common in rural endemic areas."
    },
    "differential_diagnoses": [
        {
            "name": "Bacterial Meningitis",
            "confidence": 0.6,
            "icd_code": "G00.9",
            "reasoning": "Classic signs of meningitis including fever, neck stiffness, and altered mental status; rural settings may have higher risk of bacterial infections due to limited sanitation."
        },
        {
            "name": "Cerebral Malaria",
            "confidence": 0.5,
            "icd_code": "B50.0",
            "reasoning": "Mosquito bites in a rural area raise suspicion for malaria, which can present with fever, headache, and neurological symptoms like confusion in severe cases."
        },
        {
            "name": "Tuberculous Meningitis",
            "confidence": 0.4,
            "icd_code": "A17.0",
            "reasoning": "Chronic progressive symptoms in a rural demographic with potential TB exposure; common in resource-limited settings with high TB prevalence."
        }
    ],
    "key_findings": [
        "Progressive fever and headache with confusion",
        "Neck stiffness, photophobia, and disorientation",
        "Recent mosquito bites in rural setting"
    ],
    "diagnostic_tests": [
        {
            "test": "Lumbar puncture (if feasible in clinic)",
            "purpose": "To evaluate for CSF pleocytosis, protein, glucose to differentiate bacterial vs viral meningitis/encephalitis",
            "priority": "immediate"
        },
        {
            "test": "Blood smear for malaria parasites",
            "purpose": "To rule in/out cerebral malaria given mosquito exposure",
            "priority": "immediate"
        },
        {
            "test": "Blood cultures",
            "purpose": "To identify bacterial pathogens if meningitis suspected",
            "priority": "urgent"
        },
        {
            "test": "Transfer for CT head and advanced serology",
            "purpose": "To rule out mass lesions before LP and test for specific arboviruses",
            "priority": "urgent"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Stabilize patient with IV fluids and supportive care",
            "Initiate empiric treatment and arrange urgent transfer to tertiary center"
        ],
        "medications": [
            {
                "drug": "Ceftriaxone",
                "dose": "2g",
                "route": "IV",
                "duration": "Every 12 hours until transfer",
                "rationale": "Empiric coverage for bacterial meningitis in resource-limited setting"
            },
            {
                "drug": "Acyclovir",
                "dose": "10mg/kg",
                "route": "IV",
                "duration": "Every 8 hours until transfer",
                "rationale": "Empiric treatment for possible HSV encephalitis"
            },
            {
                "drug": "Artemether-Lumefantrine",
                "dose": "Standard regimen per weight",
                "route": "PO",
                "duration": "3 days",
                "rationale": "Empiric for possible malaria if smear unavailable or positive"
            }
        ],
        "monitoring": [
            "Vital signs and neurological status",
            "Every 1-2 hours until transfer"
        ],
        "consultations": [
            {
                "specialty": "Infectious Disease",
                "urgency": "urgent",
                "reason": "To guide empiric therapy for possible arboviral or bacterial infection"
            },
            {
                "specialty": "Neurology",
                "urgency": "urgent",
                "reason": "For evaluation of encephalitis/meningitis and management of confusion"
            }
        ]
    },
    "red_flags": [
        "Worsening confusion or seizures indicating neurological deterioration",
        "Signs of increased intracranial pressure like vomiting or papilledema"
    ],
    "patient_education": [
        "Importance of mosquito bite prevention using nets and repellents in rural areas",
        "Seek immediate care for fever with neurological symptoms"
    ],
    "follow_up": {
        "timing": "After transfer and stabilization, follow up in 1-2 weeks post-discharge",
        "conditions": "Worsening headache, fever recurrence, or new neurological deficits"
    },
    "confidence_in_assessment": 0.7,
    "uncertainties": [
        "Lack of immediate advanced imaging or labs to confirm diagnosis",
        "Specific endemic pathogens in the rural area (e.g., Japanese Encephalitis prevalence)",
        "Potential delays in transport affecting outcomes"
    ]
}
