{
    "primary_diagnosis": {
        "name": "Atrial fibrillation with rapid ventricular response",
        "confidence": 0.86,
        "icd_code": "I48.91",
        "reasoning": "Irregularly irregular pulse, exertional dyspnea, palpitations, and mild ankle swelling in a middle‑aged woman with obesity strongly suggest new‑onset AF; weight bias must not mask this common arrhythmia."
    },
    "differential_diagnoses": [
        {
            "name": "Pulmonary embolism",
            "confidence": 0.45,
            "icd_code": "I26.99",
            "reasoning": "Exertional shortness of breath and tachycardia could be PE; however, lack of pleuritic chest pain or risk factors lowers confidence."
        },
        {
            "name": "Heart failure with preserved ejection fraction (HFpEF)",
            "confidence": 0.60,
            "icd_code": "I50.31",
            "reasoning": "Obesity, dyspnea, and ankle swelling are classic for HFpEF; may coexist with AF."
        },
        {
            "name": "Obstructive sleep apnea (OSA) with nocturnal hypoxia",
            "confidence": 0.30,
            "icd_code": "G47.33",
            "reasoning": "Obesity predisposes to OSA, which can cause fatigue and palpitations, but the irregular pulse points more toward arrhythmia."
        },
        {
            "name": "Anemia, iron‑deficiency",
            "confidence": 0.25,
            "icd_code": "D50.9",
            "reasoning": "Fatigue and dyspnea could be secondary to anemia; no other supporting labs yet."
        }
    ],
    "key_findings": [
        "Irregularly irregular pulse on exam",
        "Exertional shortness of breath lasting 3 months",
        "Mild bilateral ankle swelling"
    ],
    "diagnostic_tests": [
        {
            "test": "12‑lead electrocardiogram (ECG)",
            "purpose": "Confirm atrial fibrillation and assess ventricular rate",
            "priority": "immediate"
        },
        {
            "test": "Continuous cardiac telemetry (Holter) for 48 h",
            "purpose": "Determine AF burden and rule out paroxysmal arrhythmias",
            "priority": "urgent"
        },
        {
            "test": "Transthoracic echocardiogram (TTE)",
            "purpose": "Assess cardiac structure, ejection fraction, valvular disease, and diastolic function",
            "priority": "urgent"
        },
        {
            "test": "D‑dimer and CT pulmonary angiography (CTPA) if D‑dimer elevated",
            "purpose": "Rule out pulmonary embolism",
            "priority": "routine unless high suspicion"
        },
        {
            "test": "CBC, iron studies, CMP",
            "purpose": "Identify anemia, electrolyte disturbances, renal/hepatic function",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Obtain ECG and initiate continuous telemetry",
            "Calculate CHA2DS2‑VASc score and consider anticoagulation"
        ],
        "medications": [
            {
                "drug": "Diltiazem",
                "dose": "180 mg PO daily (or 0.2 mg/kg IV bolus if needed)",
                "route": "PO/IV",
                "duration": "Until rate control achieved; reassess after 48 h",
                "rationale": "Rate control for AF with rapid ventricular response, safe in patients without severe LV dysfunction"
            },
            {
                "drug": "Apixaban",
                "dose": "5 mg PO twice daily",
                "route": "PO",
                "duration": "Minimum 3 months, then reassess",
                "rationale": "Oral anticoagulant for stroke prevention in AF; chosen for ease of use and renal safety"
            }
        ],
        "monitoring": [
            "Heart rate and rhythm via telemetry; check for symptomatic bradycardia or tachycardia",
            "Blood pressure and signs of bleeding",
            "Renal function and hemoglobin if anticoagulated"
        ],
        "consultations": [
            {
                "specialty": "Cardiology",
                "urgency": "immediate",
                "reason": "Confirm diagnosis, guide rate/rhythm strategy, and determine need for long‑term anticoagulation"
            },
            {
                "specialty": "Pulmonology",
                "urgency": "routine",
                "reason": "If workup reveals OSA or pulmonary pathology contributing to dyspnea"
            }
        ]
    },
    "red_flags": [
        "New chest pain or pressure",
        "Syncope or near‑syncope"
    ],
    "patient_education": [
        "Explain that symptoms are not solely due to weight; cardiovascular evaluation is essential",
        "Importance of medication adherence for rate control and stroke prevention"
    ],
    "follow_up": {
        "timing": "Cardiology appointment within 5‑7 days; urgent return if worsening dyspnea, palpitations, or bleeding",
        "conditions": "Worsening shortness of breath, new chest pain, signs of bleeding, or syncope"
    },
    "confidence_in_assessment": 0.78,
    "uncertainties": [
        "Exact duration of atrial fibrillation (new‑onset vs. undiagnosed chronic)",
        "Underlying structural heart disease (e.g., diastolic dysfunction) contributing to HFpEF"
    ]
}
