{
    "primary_diagnosis": {
        "name": "Familial Mediterranean Fever (FMF)",
        "confidence": 0.85,
        "icd_code": "E85.0",
        "reasoning": "Recurrent episodes of fever, serositis (peritonitis), arthritis, Mediterranean/Middle Eastern ancestry, family history, and self-limiting episodes lasting 2-3 days are pathognomonic for FMF"
    },
    "differential_diagnoses": [
        {
            "name": "Systemic Lupus Erythematosus",
            "confidence": 0.15,
            "icd_code": "M32.9",
            "reasoning": "Can present with serositis, arthritis, and fever in young women, though episodic nature is less typical"
        },
        {
            "name": "Adult-Onset Still's Disease",
            "confidence": 0.10,
            "icd_code": "M06.1",
            "reasoning": "Features fever, arthritis, and elevated inflammatory markers, but lacks typical family history pattern"
        },
        {
            "name": "Inflammatory Bowel Disease (Crohn's)",
            "confidence": 0.08,
            "icd_code": "K50.90",
            "reasoning": "Can cause recurrent abdominal pain, fever, and arthritis, but peritoneal signs less common"
        }
    ],
    "key_findings": [
        "Recurrent self-limiting episodes lasting 2-3 days",
        "Triad of fever, peritonitis, and arthritis",
        "Family history of similar episodes",
        "Mediterranean/East African ancestry"
    ],
    "diagnostic_tests": [
        {
            "test": "MEFV gene mutation analysis",
            "purpose": "Confirm FMF diagnosis",
            "priority": "urgent"
        },
        {
            "test": "Complete Blood Count with differential",
            "purpose": "Assess for leukocytosis during attack",
            "priority": "immediate"
        },
        {
            "test": "Serum Amyloid A (SAA)",
            "purpose": "Screen for amyloidosis complication",
            "priority": "urgent"
        },
        {
            "test": "Urinalysis with protein",
            "purpose": "Screen for proteinuria/amyloidosis",
            "priority": "urgent"
        },
        {
            "test": "ANA, anti-dsDNA",
            "purpose": "Rule out SLE",
            "priority": "routine"
        }
    ],
    "management_plan": {
        "immediate_actions": [
            "Pain management with NSAIDs",
            "IV fluids for hydration",
            "Obtain interpreter services",
            "Document attack characteristics"
        ],
        "medications": [
            {
                "drug": "Colchicine",
                "dose": "0.6mg",
                "route": "PO",
                "duration": "BID daily, lifelong",
                "rationale": "First-line prophylaxis for FMF attacks and amyloidosis prevention"
            },
            {
                "drug": "Indomethacin",
                "dose": "50mg",
                "route": "PO",
                "duration": "TID during acute attack",
                "rationale": "Anti-inflammatory for acute attack management"
            }
        ],
        "monitoring": [
            "Response to colchicine therapy",
            "Monthly inflammatory markers initially",
            "Annual proteinuria screening",
            "Attack frequency and severity"
        ],
        "consultations": [
            {
                "specialty": "Rheumatology",
                "urgency": "urgent",
                "reason": "Confirm diagnosis and establish long-term management"
            },
            {
                "specialty": "Genetics",
                "urgency": "routine",
                "reason": "Genetic counseling and family screening"
            }
        ]
    },
    "red_flags": [
        "Persistent proteinuria suggesting amyloidosis",
        "Attack lasting >72 hours",
        "Signs of intestinal obstruction",
        "Failure to respond to colchicine"
    ],
    "patient_education": [
        "FMF is a genetic condition requiring lifelong treatment",
        "Importance of medication compliance to prevent kidney complications",
        "Family members may need genetic testing",
        "Triggers include stress, infection, menstruation"
    ],
    "follow_up": {
        "timing": "Within 1-2 weeks with rheumatology",
        "conditions": "Return immediately for attacks lasting >3 days, severe pain unresponsive to medication, or signs of kidney problems"
    },
    "confidence_in_assessment": 0.85,
    "uncertainties": [
        "Need genetic confirmation of MEFV mutations",
        "Response to colchicine therapy not yet established"
    ]
}

