        "Recurrent fever episodes lasting 1-3 days",
        "Abdominal and chest pain, breathing difficulties",
        "Joint pain in knees and ankles",
        "Positive family history of similar symptoms",
        "Elevated inflammatory markers during attacks (CRP, ESR, WBC)"
    ],
    "tests": [
        {
            "test": "Genetic testing for MEFV gene mutations (pending results)",
            "priority": "routine"
        },
        {
            "test": "Complete blood count (CBC) with differential",
            "priority": "routine"
        },
        {
            "test": "Inflammatory markers (CRP, ESR) during attack and between attacks",
            "priority": "urgent"
        },
        {
            "test": "Comprehensive metabolic panel",
            "priority": "routine"
        },
        {
            "test": "Autoantibody panel to rule out autoimmune conditions",
            "priority": "routine"
        },
        {
            "test": "Imaging (CT scan or MRI) of chest and abdomen to assess for inflammation or damage",
            "priority": "urgent"
        }
    ],
    "management": {
        "immediate": [
            "Manage pain and fever with NSAIDs during attacks: Ibuprofen 600 mg orally every 6-8 hours as needed for pain and fever relief."
        ],
        "medications": [
            "Colchicine: 0.6 mg orally twice daily for prophylaxis against FMF attacks",
            "Corticosteroids: Consider short-term course during severe attacks (e.g., Prednisone 20-40 mg orally daily, taper over 2-3 weeks)",
            "Biological agents: If colchicine-resistant or intolerant, consider IL-1 inhibitors (e.g., Anakinra, Canakinumab) under specialist supervision"
        ],
        "consultations": [
            "Rheumatology: urgent, to confirm diagnosis and initiate treatment",
            "Gastroenterology: routine, to evaluate abdominal symptoms and rule out IBD",
            "Pulmonology: routine, to assess chest pain and breathing difficulties"
        ]
    },
    "red_flags": [
        "Early-onset recurrent fever episodes",
        "Positive family history of similar symptoms",
        "Multisystem involvement (abdominal, chest, joints)",
        "Elevated inflammatory markers during attacks",
        "Patient may be at risk of amyloidosis if FMF is left untreated"
    ]
}
